import React, {Component} from "react"; 
import './../components/Css/styles.css';
import {NavLink,Link} from "react-router-dom"; 
import DataService from './../service/DataService'; 
import UploadService from "./../service/file-upload.service";
 
import line from "./../assets/images/yellowline.png";
import graphic1 from "./../assets/images/Data extraction.gif";
// import sections
import Founders_Tiles from '../components/sections/Founders_Tiles';
import Investors_Manager from '../components/sections/Investors_Manager';
import Accord from '../components/sections/Accord';
import Founders_Community from '../components/sections/Founders_Community';
import Founders_Raising from '../components/sections/Founders_Raising';
import Founders_Process from '../components/sections/Founders_Process';
import Founders_Calender from '../components/sections/Founders_Calender';


import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09

 


const extractFilename = (path) => {
  const pathArray = path.split("/");
  const lastIndex = pathArray.length - 1;
  return pathArray[lastIndex];
};

 

class Founders extends React.Component {
  constructor() {
   super(); 
   this.retriveFounders = this.retriveFounders.bind(this);
  }

componentDidMount() { 
 
   
  this.retriveFounders();

}
retriveFounders(){
}

render() { 
  return (
    <>

<div className="container-fluid ">
  <div className="col-md-12">
    <div className="row wow fadeInRight"  data-wow-delay="0.1s"> 
      <div className="col-lg-12 text-center">
          <h2 className="text-dark  text-center h2line">
            Community = Capital
          </h2>  
          <div className="d-flex justify-content-center  text-center">
            <img className="img-fluid" src={line} alt="line " />
          </div>
      </div> 
      </div>  
    {/*  Row */}
    <div className="row wow fadeInRight bg-gold"  data-wow-delay="0.1s"> 
       
       <div className="col-md-8" >
        <div className="d-flex justify-content-center mt-5 mb-5 text-center"> 
    
            <img src={graphic1}/>  <br/>


    
        </div> 
        <div className="row d-flex justify-content-center text-center">
        <h5 class="text-white col-md-6">Use the power of community and raise funds while growing
        your brand, & increasing sales.</h5>


</div>
       </div>
       <div className="col-md-4" > 
       <div class="card  profile-card-4" >
{/* <img src="..." class="card-img-top" alt="..."/> */}
<div class="card-body text-center">
<h6 class="card-title"><span className="profile-card-2"><i className="fa fa-cloud text-primary"></i></span>&nbsp;Your Startup</h6>
 
<div  class="row">
  <div class="col-md-6">
  <span className="text-secondary font12">COMMUNITY</span>
    </div>
    <div class="col-md-6">
    <span className="text-primary font12">+100 Members</span>
    </div>
  </div>
  
</div>
<div class="row">
  <marquee direction="up"  loop="true"> 
<div class="d-flex  justify-content-center">
      <div class="flex-shrink-0 circle-mt-img">
      <img class="mr-3" src={require('./../assets/blurimages/1.jpg')} alt="Generic placeholder image" width={32}
                  height={32}/>
      </div>
      <div class="  ms-3">
        <h5 class="mt-0 para font12">A.D.<br/>
        <span className=" text-muted para font12">INVESTED <i className="fa fa-rupee"></i>&nbsp;38,000</span>
      </h5>
        </div> </div>
         


        <div class="d-flex  justify-content-center">
        <div class="flex-shrink-0 circle-mt-img">
      <img class="mr-3" src={require('./../assets/blurimages/2.jpg')} alt="Generic placeholder image" width={32}
                  height={32}/>
      </div>
      <div class="  ms-3">
        <h5 class="mt-0 para font14 b">P.A.<br/>
        <span className=" text-muted para  font14">INVESTED <i className="fa fa-rupee"></i>&nbsp;43,000</span>
      </h5>
        </div> </div>
         

        <div class="d-flex  justify-content-center">
        <div class="flex-shrink-0 circle-mt-img">
      <img class="mr-3"  src={require('./../assets/blurimages/3.jpg')} alt="Generic placeholder image" width={32}
                  height={32}/>
      </div>
      <div class="  ms-3">
        <h5 class="mt-0 para  font14 b">P.A.<br/>
        <span className=" text-muted para  font14">INVESTED <i className="fa fa-rupee"></i>&nbsp;13,000</span>
      </h5>
        </div> 
</div>
  </marquee>
  
    <p  className="para text-muted text-center">*In the initial rounds</p>
  <div class="d-flex text-center justify-content-center">
  <div class="col-md-6">
  <span className="text-secondary para text-muted">INVESTORS</span><br/>
  <span className="text-dark para b">326</span>
    </div>
    <div class="col-md-6">
    <span className="text-secondary para">AMT RAISED</span><br/>
    <span className="text-dark para b">2.4 Million</span>
    </div>
  </div>
</div>

 
{/* <div class="card-body">
<a href="#" class="card-link">Card link</a>
<a href="#" class="card-link">Another link</a>
</div> */}
</div>
        </div>
     </div> 
   


  </div>
</div>



    <Founders_Community split/>
    <Founders_Tiles topDivider/>
    <Founders_Calender/>
    <Founders_Process/>
    {/* <Founders_Raising/> */}
    <Investors_Manager split/>
    <Accord/>
   </>
    ) ;       
  }
 };
 
 
  
export default Founders;
