validateTeamInfo(){
  console.log("inside Team info validations")
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      $("#TeamInfoform #tmname").each(function (index) {
        if (window.isEmpty($($("#tmname")[index]).val())) {
          isValid = false;
          errors["tmname"]= "Please enter your Team Member Name.";
        } 
    
        if (window.isEmpty($($("#tmposition")[index]).val())) {
          isValid = false;
          errors["tmposition"]= "Please enter Position Name.";
        }
     
        if (window.isEmpty($($("#tmfblink")[index]).val())) {
          isValid = false;
          errors["tmfblink"] = "Please enter your Facebook Link.";
        }
     
        if (window.isEmpty($($("#tminstalink")[index]).val())) {
          isValid = false;
          errors["tminstalink"][index] = "Please enter your Instagram Link.";
        }
     
        if (window.isEmpty($($("#tmlinklink")[index]).val())) {
          isValid = false;
          errors["tmlinklink"][index] = "Please enter your Linked In Link.";
        }
     
        if (window.isEmpty($($("#tmteambio")[index]).val())) {
          isValid = false;
          errors["tmteambio"][index] = "Please Enter Your Team Bio ";
        }
      });
      
     
       
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
