import React, {useState} from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import UploadService from "../../service/file-upload.service";
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09

class Campaign_FAQ extends React.Component {
    constructor() {
    super();
    this.state = {
      campfaqAllDets: [],
      currenfaqinfo: null,
      currenfaqinfoIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      CFAQ_SNO:"",
      CFAQ_QUESTION:"", 
      CFAQ_QANSWER:"", 
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:"",  
      CFAQ_PROFILE_NAME:"",  
    };
    
    this.saveCamFaq = this.saveCamFaq.bind(this);  
    this.retrieveAllFaq = this.retrieveAllFaq.bind(this);
    this.setActiveFaq = this.setActiveFaq.bind(this);
    // this.handleImagePreview=this.handleImagePreview.bind(this)
     
  }
  
  
  componentDidMount() {
    this.retrieveAllFaq(); 
  }

  retrieveAllFaq() {
    var querySet = "/camfaq?EMAIL="+sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
     .then(response => {
       this.setState({
        campfaqAllDets: response.data, 
       });
       
       $.each(this.state.campfaqAllDets, function (index, value) {
         
        if(index != 0){
          $("#camfaqAddmem").click(); 
        } 
          $($("#CamFaqform #campfaqid")[index]).attr("value",value.ID).val(value.ID);
          $($("#CamFaqform #spantCFAQestsno")[index]).attr("value",value.CFAQ_SNO).val(value.CFAQ_SNO);
          $($("#CamFaqform #camfaqsno")[index]).attr("value",value.CFAQ_SNO).val(value.CFAQ_SNO);
          $($("#CamFaqform #camfaqquestion")[index]).attr("value",value.CFAQ_QUESTION).val(value.CFAQ_QUESTION);
          $($("#CamFaqform #camfaqquestanswer")[index]).attr("value",value.CFAQ_QANSWER).val(value.CFAQ_QANSWER);
           
        }); 
    
     })
     .catch(e => {
       console.log(e);
     });
 }

 

 setActiveFaq(campfaqAllDets, index) {
  this.setState({
    currenfaqinfo: campfaqAllDets,
    currenfaqinfoIndex: index,
    // tfcurrentFile:this.state.tfcurrentFile
  });
} 
     

saveCamFaq(event) {
  event.preventDefault();  
  console.log("saveCamFaq");
  if(this.validateCamFaq()){ 
  if(!window.isEmpty(sessionStorage.getItem("sessEmail"))){  
     
    $("#CamFaqform #camfaqquestion").each(function (teamset) { 
       

    let camInvformData = new FormData();  
    camInvformData.append("ID",Number($($("#CamFaqform #campfaqid")[teamset]).val()));
    camInvformData.append("MTUSER_ID",$("#mtuser_id").val());
    camInvformData.append("EMAIL",$("#mtuser_email").val()); 
    camInvformData.append("MODULE",$("#mtuser_module").val());
    camInvformData.append("CFAQ_SNO",$($("#CamFaqform #camfaqsno")[teamset]).val());
    camInvformData.append("CFAQ_QUESTION",$($("#CamFaqform #camfaqquestion")[teamset]).val());
    camInvformData.append("CFAQ_QANSWER",$($("#CamFaqform #camfaqquestanswer")[teamset]).val()); 
    // camInvformData.append("CFAQ_PROFILE_PIC",null,""); 
    camInvformData.append("STATUS","Active");
    camInvformData.append("COMMENTS","TEST");
    camInvformData.append("DESCRIPTION","TEST");
    camInvformData.append("CREATED_USER",$("#mtuser_fname").val());
    camInvformData.append("CREATED_DATE",Moment(date).format("YYYY-MM-DD"));
    camInvformData.append("MODIFIED_USER",$("#mtuser_fname").val());
    camInvformData.append("MODIFIED_DATE",Moment(date).format("YYYY-MM-DD")); 

    if(window.isEmpty($($("#CamFaqform #campfaqid")[teamset]).val())){ 
      camInvformData.append("CFAQ_PROFILE_PIC",$("#CamFaqform #caminvupdprofile")[teamset].files[0],$("#CamFaqform #caminvupdprofile")[teamset].files[0].name); 
    } 
       
 
  if(window.isEmpty($($("#CamFaqform #campfaqid")[teamset]).val())){  
    console.log("insert"+camInvformData);
    UploadService.create("/camfaq",camInvformData)
    .then(response => {  
        window.showLoader();  
        window.showAlert("Campaign Investor Information is submittted","/Campaign_Investors");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/Campaign_Investors");
    });

  }else{ 
    console.log("update"+camInvformData);
    
    UploadService.update("/camfaq/"+Number($($("#CamFaqform #campfaqid")[teamset]).val()),camInvformData)
    .then(response => {  
        window.showLoader();  
      window.showAlert("Campaign Investor Information is submittted","/Campaign_Investors");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/Campaign_Investors");
    });
    
  }


      });
    
   

  }else{
    window.showAlert("Please Login","/Login");
  }
}

}  
validateCamFaq(){
  console.log("inside validateCamFaq")
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      $("#CamFaqform #camfaqquestion").each(function (index) {
        if (window.isEmpty($($("#CamFaqform #camfaqquestion")[index]).val())) {
          isValid = false; 
          // errors["camfaqquestion"]= "Please enter your Team Member Name.";
          $($("#CamFaqform #camfaqquestion")[index]).parent().find("div").html("Please enter your Team Member's Name.");
         } 
    
        if (window.isEmpty($($("#CamFaqform #camivstposition")[index]).val())) {
          isValid = false;
          // errors["camivstposition"]= "Please enter Position Name.";
          $($("#CamFaqform #camivstposition")[index]).parent().find("div").html("Please enter Position Name.");
       
        }
     
        if (window.isEmpty($($("#CamFaqform #camfblink")[index]).val())) {
          isValid = false;
          // errors["camfblink"] = "Please enter your Facebook Link.";
          $($("#CamFaqform #camfblink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Facebook Link.");
        }
     
        if (window.isEmpty($($("#CamFaqform #caminvinstalink")[index]).val())) {
          isValid = false;
          // errors["caminvinstalink"] = "Please enter your Instagram Link.";
          $($("#CamFaqform #caminvinstalink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Instagram Link.");
        }
     
        if (window.isEmpty($($("#CamFaqform #caminvlinklink")[index]).val())) {
          isValid = false;
          // errors["caminvlinklink"] = "Please enter your Linked In Link.";
          $($("#CamFaqform #caminvlinklink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Linked In Link.");
        }
     
        if (window.isEmpty($($("#CamFaqform #camfaqquestanswer")[index]).val())) {
          isValid = false;
          // errors["camfaqquestanswer"] = "Please Enter Your Team Bio ";
          $($("#CamFaqform #camfaqquestanswer")[index]).parent().find("div").html("Please Enter Your Team Bio");
        }
      
      
        if (window.isEmpty($("#CamFaqform #campfaqid").val())) {
        if (!Number($($("#CamFaqform #caminvupdprofile")[index]).get(0).files.length)  > 0) {
          isValid = false;
          // errors["caminvupdprofile"] = "Please Upload Image";
          $($("#CamFaqform #caminvupdprofiles")[index]).parent().find("div").html("Please Upload Image");
          $("#CamFaqform #caminvupdprofile").focus();
        }
        }
      
      });
      
       
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
 render() {
 
  const {  campfaqAllDets, currenfaqinfo, currenfaqinfoIndex } = this.state;
    return (
        <>
        <body className='' >
            <div className='container'>  
                <div className='row' style={{height:'auto',marginTop:100}}>
                    <div className='row'>
                      <div className="hero-content">
                         <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item">
                                      <NavLink to="/Campaign" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>&nbsp;Back to Dashboard</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
                            </nav>

                           <div className='container'>
                              <div className='row' style={{height:'auto'}}>
                              
                                    <div className='col-md-12'>
                                        <h3>Investors</h3>
                                        {/* <p className='para'>
                                          Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                          Tincidunt libero vel elementum at cum tupis eget. Viverra ultrices lacus,
                                           lectus volutpat sociis vitae mauris<br/>porta faugiat. Nec, vitae facilisi 
                                           elementum eu est vel quis platea. Diam pharetra nec malesuada mi purus erat.</p>
                                    
                                         */}
                                        
                                     <div className='row'>
                                     
                                     <form name="CamFaqform" id="CamFaqform" method="POST" encType="multipart/form-data" className="row m-5 g-3"  >  

                                        <div className="col-md-12 campinvestmem" id="campinvestDivisionIter">
                                        <span id="spantCFAQestsno" className='spanbgcircle'>1</span> 
                                          
                                            <div className="col-md-6 mb-3 mt-3">
                                            <label htmlFor="camfaqquestion" className="form-label">Name</label>
                                            <input name="campfaqid" id="campfaqid" type="hidden"  />
                                            <input name="camfaqsno" id="camfaqsno" type="hidden" value="1"/>
                                            <input
                                                  name='camfaqquestion' 
                                                  type="text" 
                                                  value={this.state.input.camfaqquestion}
                                                  onChange={this.handleChange}
                                                  className="form-control"  
                                                  maxLength="150"  
                                                  placeholder="Enter Your Team Member Name" 
                                                  id='camfaqquestion'/>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.camfaqquestion}</div>
                                            </div> 

                                            
                                            <div className="col-md-6 mb-3 mt-3">
                                            <label htmlFor="camivstposition" className="form-label">Position in the Company</label>
                                            <input
                                                 name='camivstposition' 
                                                 type="text"
                                                 value={this.state.input.camivstposition}
                                                 onChange={this.handleChange} 
                                                 className="form-control"  
                                                 maxLength="150"  
                                                 placeholder="Position Name"
                                                 id='camivstposition'/>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.camivstposition}</div>
                                            </div> 

                                       
                                        <div className="col-md-6 mb-3 mt-3"> 
                                        <div className="input-group "><Image   src={require('./../../assets/images/face.png')}alt="Features tile icon 01"  width={50} height={50}/>
                                            <input
                                                 name='camfblink' 
                                                 value={this.state.input.camfblink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control link"  
                                                 maxLength="150"  
                                                 placeholder="Facebook Link"
                                                 id='camfblink'/>
                                                    </div>
                                                    <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.camfblink}</div>
                                              
                                            </div>
                                             
                                        
                                            <div className="col-md-6 mb-3 mt-3">
                                            <div className="input-group ">
                                              <Image   src={require('./../../assets/images/insta.jpg')}alt="Features tile icon 01"  width={50} height={50}/> 
                                            <input
                                                 name='caminvinstalink' 
                                                 value={this.state.input.caminvinstalink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control link"  
                                                 maxLength="150"  
                                                 placeholder="Instagram Link"
                                                 id='caminvinstalink'/>
                                                 
                                                 </div>
                                                     <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.caminvinstalink}</div>
                                            </div> 

                                            <div className="col-md-6 mb-3 mt-3">
                                            <div className="input-group ">
                                              <Image   src={require('./../../assets/images/lin.png')}alt="Features tile icon 01"  width={50} height={50}/> 
                                            <input
                                                  name='caminvlinklink' 
                                                  value={this.state.input.caminvlinklink}
                                                  onChange={this.handleChange}
                                                  type="text" 
                                                  className="form-control link"  
                                                  maxLength="150"  
                                                  placeholder="LinkedIn Link"
                                                  id='caminvlinklink'/>
                                                  </div>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.caminvlinklink}</div>
                                              
                                            </div>
                                           
                                            <div className="col-md-6 mb-3 mt-3">
                                          <label htmlFor="camfaqquestanswer" className="form-label">Bio</label>
                                          
                                            <textarea  name='camfaqquestanswer' 
                                                 value={this.state.input.bio}
                                                  onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control"  
                                                 maxLength="250"  
                                                 rows="10"
                                                 placeholder="Type something about you team member..." 
                                                 id='camfaqquestanswer'></textarea> 
                                             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.camfaqquestanswer}</div>
                                              
                                            </div>

                                          <div className="col-md-6 mb-3 mt-3">
                                            <div className="row mb-3">
                                            <div className="col-md-6">
                                            <label htmlFor="caminvupdprofile" className="col-sm-2 form-label">Profile</label>
                                            </div>

                                            <div className="col-md-6 imgUp">    
                                                      <div className="imagePreview"></div>
                                                  <label   className="col-md-12 btn btn-outline-secondary btn-sm">
                                                        Upload<br/>
                                                        <input type="file" id="caminvupdprofile" name="caminvupdprofile" 
                                                        accept="image/*" data-attr="1" className=" uploadFile img"  multiple 
                                                        style={{height:"0px",width:"0px",overflow : "hidden"}}/>
                                                          </label>
                                                          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.caminvupdprofile}</div>
                                                    </div>   
 
                                            </div>
                                          </div>
                                             
                                         </div>
                                         <div className="col-md-12">
                                         <div className="row">
  <div className="col-md-3 mb-3" >
  <button type="button" className="btn btn-outline-secondary btn-sm w-100" data-attr-id="campinvestDivisionIter" id="camfaqAddmem">Add Another</button>
    </div>
    <div className="col-md-3 mb-3" >
  <button type="button" className="btn btn-success btn-sm  w-100"  onClick={this.saveCamFaq}>Submit</button>
    </div>


  </div>
                                         </div>

                                      
                                        </form>
                                        </div>
                                     </div>
                                     
                                     
                                 </div>
                                 
                              
                             </div>
                             
                        </div>
                       
                     </div>
                    
                </div>      
            
            </div> 

                    
                     
        </body>


            
            
        </>
    );

  }
};






export default Campaign_FAQ;
