import React, {useState} from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import UploadService from "../../service/file-upload.service";
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09

class Campaign_Investors extends React.Component {
    constructor() {
    super();
    this.state = {
      campInvestAllDets: [],
      currentInvestinfo: null,
      currentInvestinfoIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      CINV_SNO:"",
      CINV_MEMBER_NAME:"",
      CINV_MEMBER_POSITION:"",
      CINV_FB_LINK:"",
      CINV_INSTA_LINK:"",
      CINV_LINKEDIN_LINK:"",
      CINV_BIO:"",
      CINV_PROFILE_PIC:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:"",  
      CINV_PROFILE_NAME:"",  
    };
    
    this.saveCamInv = this.saveCamInv.bind(this);  
    this.retrieveAllCamInv = this.retrieveAllCamInv.bind(this);
    this.setActiveCamInv = this.setActiveCamInv.bind(this);
    // this.handleImagePreview=this.handleImagePreview.bind(this)
     
  }
  
  
  componentDidMount() {
    this.retrieveAllCamInv(); 
  }

  retrieveAllCamInv() {
    var querySet = "/caminvest?EMAIL="+sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
     .then(response => {
       this.setState({
        campInvestAllDets: response.data, 
       });
       
       $.each(this.state.campInvestAllDets, function (index, value) {
         
        if(index != 0){
          $("#caminvestAddmem").click(); 
        }
  //  var mobile = value.ID;
  //  sessionStorage.setItem("mobile", mobile);
          $($("#CamInvestform #campinvestid")[index]).attr("value",value.ID).val(value.ID);
          $($("#CamInvestform #spantcinvestsno")[index]).attr("value",value.CINV_SNO).val(value.CINV_SNO);
          $($("#CamInvestform #camivstsno")[index]).attr("value",value.CINV_SNO).val(value.CINV_SNO);
          $($("#CamInvestform #camivstname")[index]).attr("value",value.CINV_MEMBER_NAME).val(value.CINV_MEMBER_NAME);
          $($("#CamInvestform #tmposition")[index]).attr("value",value.CINV_MEMBER_POSITION).val(value.CINV_MEMBER_POSITION);
          $($("#CamInvestform #tmfblink")[index]).attr("value",value.CINV_FB_LINK).val(value.CINV_FB_LINK);
          $($("#CamInvestform #tminstalink")[index]).attr("value",value.CINV_INSTA_LINK).val(value.CINV_INSTA_LINK);
          $($("#CamInvestform #tmlinklink")[index]).attr("value",value.CINV_LINKEDIN_LINK).val(value.CINV_LINKEDIN_LINK);
          $($("#CamInvestform #tmteambio")[index]).attr("value",value.CINV_BIO).val(value.CINV_BIO);
          $($(".imagePreview")[index]).css("background-image","url("+window.mt_backend_url+value.CINV_PROFILE_PIC+")");
          $($("#CamInvestform #tmuploadprofile")[index]).closest("label").css("visibility","hidden");


        }); 
    
     })
     .catch(e => {
       console.log(e);
     });
 }

 

 setActiveCamInv(campInvestAllDets, index) {
  this.setState({
    currentInvestinfo: campInvestAllDets,
    currentInvestinfoIndex: index,
    // tfcurrentFile:this.state.tfcurrentFile
  });
} 
     

saveCamInv(event) {
  event.preventDefault();  
  if(this.validateTeamInfo()){ 
  if(!window.isEmpty(sessionStorage.getItem("sessEmail"))){  
     
    $("#CamInvestform #camivstname").each(function (teamset) { 
       

    let tfformData = new FormData();  
    tfformData.append("ID",$($("#CamInvestform #campinvestid")[teamset]).val());
    tfformData.append("MTUSER_ID",$("#mtuser_id").val());
    tfformData.append("EMAIL",$("#mtuser_email").val()); 
    tfformData.append("MODULE",$("#mtuser_module").val());
    tfformData.append("CINV_SNO",$($("#CamInvestform #camivstsno")[teamset]).val());
    tfformData.append("CINV_MEMBER_NAME",$($("#CamInvestform #camivstname")[teamset]).val());
    tfformData.append("CINV_MEMBER_POSITION",$($("#CamInvestform #tmposition")[teamset]).val());
    tfformData.append("CINV_FB_LINK",$($("#CamInvestform #tmfblink")[teamset]).val());
    tfformData.append("CINV_INSTA_LINK",$($("#CamInvestform #tminstalink")[teamset]).val());
    tfformData.append("CINV_LINKEDIN_LINK",$($("#CamInvestform #tmlinklink")[teamset]).val());
    tfformData.append("CINV_BIO",$($("#CamInvestform #tmteambio")[teamset]).val()); 
    // tfformData.append("CINV_PROFILE_PIC",null,""); 
    tfformData.append("STATUS","Active");
    tfformData.append("COMMENTS","TEST");
    tfformData.append("DESCRIPTION","TEST");
    tfformData.append("CREATED_USER",$("#mtuser_fname").val());
    tfformData.append("CREATED_DATE",Moment(date).format("YYYY-MM-DD"));
    tfformData.append("MODIFIED_USER",$("#mtuser_fname").val());
    tfformData.append("MODIFIED_DATE",Moment(date).format("YYYY-MM-DD")); 

    if(window.isEmpty($($("#CamInvestform #campinvestid")[teamset]).val())){ 
      tfformData.append("CINV_PROFILE_PIC",$("#CamInvestform #tmuploadprofile")[teamset].files[0],$("#CamInvestform #tmuploadprofile")[teamset].files[0].name); 
    } 
       
 
  if(window.isEmpty($($("#CamInvestform #campinvestid")[teamset]).val())){ 
    
    

    UploadService.create("/caminvest",tfformData)
    .then(response => {  
        window.showLoader();  
        window.showAlert("Team Information is submittted","/Startup_Dashboard");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/CINV_Info");
    });

  }else{ 

    
    UploadService.update("/caminvest/"+Number($($("#CamInvestform #campinvestid")[teamset]).val()),tfformData)
    .then(response => {  
        window.showLoader();  
      window.showAlert("Team Information is submittted","/Startup_Dashboard");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/CINV_Info");
    });
    
  }


      });
    
   

  }else{
    window.showAlert("Please Login","/Login");
  }
}

}  
validateTeamInfo(){
  console.log("inside Team info validations")
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      $("#CamInvestform #camivstname").each(function (index) {
        if (window.isEmpty($($("#CamInvestform #camivstname")[index]).val())) {
          isValid = false; 
          // errors["camivstname"]= "Please enter your Team Member Name.";
          $($("#CamInvestform #camivstname")[index]).parent().find("div").html("Please enter your Team Member Name.");
         } 
    
        if (window.isEmpty($($("#CamInvestform #tmposition")[index]).val())) {
          isValid = false;
          // errors["tmposition"]= "Please enter Position Name.";
          $($("#CamInvestform #tmposition")[index]).parent().find("div").html("Please enter Position Name.");
       
        }
     
        if (window.isEmpty($($("#CamInvestform #tmfblink")[index]).val())) {
          isValid = false;
          // errors["tmfblink"] = "Please enter your Facebook Link.";
          $($("#CamInvestform #tmfblink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Facebook Link.");
        }
     
        if (window.isEmpty($($("#CamInvestform #tminstalink")[index]).val())) {
          isValid = false;
          // errors["tminstalink"] = "Please enter your Instagram Link.";
          $($("#CamInvestform #tminstalink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Instagram Link.");
        }
     
        if (window.isEmpty($($("#CamInvestform #tmlinklink")[index]).val())) {
          isValid = false;
          // errors["tmlinklink"] = "Please enter your Linked In Link.";
          $($("#CamInvestform #tmlinklink")[index]).parent().parent().find("div:eq(1)").html("Please enter your Linked In Link.");
        }
     
        if (window.isEmpty($($("#CamInvestform #tmteambio")[index]).val())) {
          isValid = false;
          // errors["tmteambio"] = "Please Enter Your Team Bio ";
          $($("#CamInvestform #tmteambio")[index]).parent().find("div").html("Please Enter Your Team Bio");
        }
      });
      
     
       
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
 render() {
 
  const {  campInvestAllDets, currentInvestinfo, currentInvestinfoIndex } = this.state;
    return (
        <>
        <body className='' >
            <div className='container'>  
                <div className='row' style={{height:'auto',marginTop:100}}>
                    <div className='row'>
                      <div className="hero-content">
                         <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item">
                                      <NavLink to="/Campaign" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>&nbsp;Back to Dashboard</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
                            </nav>

                           <div className='container'>
                              <div className='row' style={{height:'auto'}}>
                              
                                    <div className='col-md-12'>
                                        <h3>Investors</h3>
                                        {/* <p className='para'>
                                          Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                          Tincidunt libero vel elementum at cum tupis eget. Viverra ultrices lacus,
                                           lectus volutpat sociis vitae mauris<br/>porta faugiat. Nec, vitae facilisi 
                                           elementum eu est vel quis platea. Diam pharetra nec malesuada mi purus erat.</p>
                                    
                                         */}
                                        
                                     <div className='row'>
                                     
                                     <form name="CamInvestform" id="CamInvestform" method="POST" enctype="multipart/form-data" className="row m-5 g-3"  >  

                                        <div className="col-md-12 teaminfomem" id="teamInfoDivisionIter">
                                        <span id="spantcinvestsno" className='spanbgcircle'>1</span> 
                                          
                                            <div className="col-md-6 mb-3 mt-3">
                                            <label for="camivstname" className="form-label">Name</label>
                                            <input name="campinvestid" id="campinvestid" type="hidden"  />
                                            <input name="camivstsno" id="camivstsno" type="hidden" value="1"/>
                                            <input
                                                  name='camivstname' 
                                                  type="text" 
                                                  value={this.state.input.camivstname}
                                                  onChange={this.handleChange}
                                                  className="form-control"  
                                                  maxlength="150"  
                                                  placeholder="Enter Your Team Member Name" 
                                                  id='camivstname'/>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.camivstname}</div>
                                            </div> 

                                            
                                            <div className="col-md-6 mb-3 mt-3">
                                            <label for="tmposition" className="form-label">Position in the Company</label>
                                            <input
                                                 name='tmposition' 
                                                 type="text"
                                                 value={this.state.input.tmposition}
                                                 onChange={this.handleChange} 
                                                 className="form-control"  
                                                 maxlength="150"  
                                                 placeholder="Position Name"
                                                 id='tmposition'/>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmposition}</div>
                                            </div> 

                                       
                                        <div className="col-md-6 mb-3 mt-3"> 
                                        <div className="input-group "><Image   src={require('./../../assets/images/face.png')}alt="Features tile icon 01"  width={50} height={50}/>
                                            <input
                                                 name='tmfblink' 
                                                 value={this.state.input.tmfblink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control"  
                                                 maxlength="150"  
                                                 placeholder="Facebook Link"
                                                 id='tmfblink'/>
                                                    </div>
                                                    <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmfblink}</div>
                                              
                                            </div>
                                             
                                        
                                            <div className="col-md-6 mb-3 mt-3">
                                            <div className="input-group ">
                                              <Image   src={require('./../../assets/images/insta.jpg')}alt="Features tile icon 01"  width={50} height={50}/> 
                                            <input
                                                 name='tminstalink' 
                                                 value={this.state.input.tminstalink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control"  
                                                 maxlength="150"  
                                                 placeholder="Instagram Link"
                                                 id='tminstalink'/>
                                                 
                                                 </div>
                                                     <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tminstalink}</div>
                                            </div> 

                                            <div className="col-md-6 mb-3 mt-3">
                                            <div className="input-group ">
                                              <Image   src={require('./../../assets/images/lin.png')}alt="Features tile icon 01"  width={50} height={50}/> 
                                            <input
                                                  name='tmlinklink' 
                                                  value={this.state.input.tmlinklink}
                                                  onChange={this.handleChange}
                                                  type="text" 
                                                  className="form-control"  
                                                  maxlength="150"  
                                                  placeholder="LinkedIn Link"
                                                  id='tmlinklink'/>
                                                  </div>
                                                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmlinklink}</div>
                                              
                                            </div>
                                           
                                            <div className="col-md-6 mb-3 mt-3">
                                          <label for="tmteambio" className="form-label">Bio</label>
                                          
                                            <textarea  name='tmteambio' 
                                                 value={this.state.input.bio}
                                                  onChange={this.handleChange}
                                                 type="text" 
                                                 className="form-control"  
                                                 maxlength="250"  
                                                 rows="10"
                                                 placeholder="Type something about you team member..." 
                                                 id='tmteambio'></textarea> 
                                             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmteambio}</div>
                                              
                                            </div>

                                          <div className="col-md-6 mb-3 mt-3">
                                            <div className="row mb-3">
                                            <div className="col-md-6">
                                            <label for="tmuploadprofile" className="col-sm-2 form-label">Profile</label>
                                            </div>

                                            <div className="col-md-6 imgUp">    
                                                      <div className="imagePreview"></div>
                                                  <label   className="col-md-12 btn btn-outline-secondary btn-sm">
                                                        Upload<br/>
                                                        <input type="file" id="tmuploadprofile" name="tmuploadprofile" 
                                                        accept="image/*" data-attr="1" className=" uploadFile img"  multiple 
                                                        style={{height:"0px",width:"0px",overflow : "hidden"}}/>
                                                          </label>
                                                          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmuploadprofile}</div>
                                                    </div>   
 
                                            </div>
                                          </div>
                                             
                                         </div>
                                         <div className="col-md-12">
                                         <div className="row">
  <div className="col-md-3 mb-3" >
  <button type="button" className="btn btn-outline-secondary btn-sm w-100" data-attr-id="teamInfoDivisionIter" id="caminvestAddmem">Add Another</button>
    </div>
    <div className="col-md-3 mb-3" >
  <button type="button" className="btn btn-success btn-sm  w-100"  onClick={this.saveCamInv}>Submit</button>
    </div>


  </div>
                                         </div>

                                      
                                        </form>
                                        </div>
                                     </div>
                                     
                                     
                                 </div>
                                 
                              
                             </div>
                             
                        </div>
                       
                     </div>
                    
                </div>      
            
            </div> 

                    
                     
        </body>


            
            
        </>
    );

  }
};






export default Campaign_Investors;
