import React from 'react';
import '../Css/styles.css'; 
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import UploadService from "../../service/file-upload.service";
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09


class Raise extends React.Component {
  constructor() {
    super();
    this.state = {
      raiseAllDets: [],
      currentraise: null,
      currentraiseIndex: -1,
      input: {},
      errors: {}, 
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      RAISE_NAME:"",
      RAISE_EMAIL:"",
      RAISE_FOUNDER_URL1:"",
      RAISE_FOUNDER_URL2:"",
      RAISE_COMPANY_NAME:"",
      RAISE_COM_LINKPAGE:"",
      RAISE_WEBSITE:"",
      RAISE_FUNDRAISING:"",
      RAISE_PROD_DESC:"",
      RAISE_TRACTION:"",
      RAISE_REVENUE:"",
      RAISE_TEAM:"",
      RAISE_COMROUND:"",
      RAISE_MTRIGHTS:"",
      RAISE_EXIST_COM:"",
      RAISE_PRIVATEROUND:"",
      RAISE_UPLOAD_DOC:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:"",  
      RAISE_UPLOAD_DOC_NAME:"",
    };
     
    this.saveRaise = this.saveRaise.bind(this);  
    this.retrieveAllRaise = this.retrieveAllRaise.bind(this);
    this.setActiveRaise = this.setActiveRaise.bind(this);
   
  }
     
  
  componentDidMount() {
    this.retrieveAllRaise(); 
  }
  
  retrieveAllTeamInfo() {
    var querySet = "/raisedets?EMAIL="+sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
     .then(response => {
       this.setState({
        raiseAllDets: response.data, 
       });
       
      //  $.each(this.state.raiseAllDets, function (index, value) {
         
        // if(index != 0){
        //   $("#teaminfoAddmem").click(); 
        // } 


          $("#Raiseform #raiseid").attr("value",response.data.ID).val(response.data.ID);
          $("#Raiseform #rsName").attr("value",response.data.RAISE_NAME).val(response.data.RAISE_NAME);
          $("#Raiseform #rsemail").attr("value",response.data.RAISE_EMAIL).val(response.data.RAISE_EMAIL);
          $("#Raiseform #rsfounderurl1").attr("value",response.data.RAISE_FOUNDER_URL1).val(response.data.RAISE_FOUNDER_URL1);
          $("#Raiseform #rsfounderurl2").attr("value",response.data.RAISE_FOUNDER_URL2).val(response.data.RAISE_FOUNDER_URL2);
          $("#Raiseform #rsRegCompanyname").attr("value",response.data.RAISE_COMPANY_NAME).val(response.data.RAISE_COMPANY_NAME);
          $("#Raiseform #rscomLinkpage").attr("value",response.data.RAISE_COM_LINKPAGE).val(response.data.RAISE_COM_LINKPAGE);
          $("#Raiseform #rswebsite").attr("value",response.data.RAISE_WEBSITE).val(response.data.RAISE_WEBSITE);
          $("#Raiseform #rsfundraisingrd").attr("value",response.data.RAISE_FUNDRAISING).val(response.data.RAISE_FUNDRAISING);
          $("#Raiseform #rsproductdesc").attr("value",response.data.RAISE_PROD_DESC).val(response.data.RAISE_PROD_DESC);
          $("#Raiseform #rstraction").attr("value",response.data.RAISE_TRACTION).val(response.data.RAISE_TRACTION);
          $("#Raiseform #rsrevenue").attr("value",response.data.RAISE_REVENUE).val(response.data.RAISE_REVENUE);
          $("#Raiseform #rsteam").attr("value",response.data.RAISE_TEAM).val(response.data.RAISE_TEAM);
          $("#Raiseform #rscommround").attr("value",response.data.RAISE_COMROUND).val(response.data.RAISE_COMROUND);
          $("#Raiseform #rsmtrights").attr("value",response.data.RAISE_MTRIGHTS).val(response.data.RAISE_MTRIGHTS);
          $("#Raiseform #rsexistingcomm").attr("value",response.data.RAISE_EXIST_COM).val(response.data.RAISE_EXIST_COM);
          $("#Raiseform #rsprivateround").attr("value",response.data.RAISE_PRIVATEROUND).val(response.data.RAISE_PRIVATEROUND);
          // $("#Raiseform #rsuploadfile").attr("value",response.data.TEAM_BIO).val(response.data.TEAM_BIO);
          // $($(".imagePreview")[index]).css("background-image","url("+window.mt_backend_url+response.data.PROFILE_PIC+")");
          // $($("#TeamInfoform #tmuploadprofile")[index]).closest("label").css("visibility","hidden");
        // }); 
    
     })
     .catch(e => {
       console.log(e);
     });
 }

 setActiveRaise(raiseAllDets, index) {
  this.setState({
    currentraise: raiseAllDets,
    currentraiseIndex: index,
    // tfcurrentFile:this.state.tfcurrentFile
  });
} 
     




saveRaise(event) {
  event.preventDefault();  
  if(this.validateRaise()){ 
  if(!window.isEmpty(sessionStorage.getItem("sessEmail"))){  
     
    // $("#TeamInfoform #tmname").each(function (teamset) { 
      var rsupProId = $("#Raiseform #rsuploadfile")
      var rslengUpPro = rsupProId[0].files.length;
      var RSFiles = rsupProId[0].files;
      var profileRSName = "";
      if (rslengUpPro > 0) {
        for (var i = 0; i < rslengUpPro; i++) {
          var fileName = RSFiles[i].name; // get file name
          var fileSize = RSFiles[i].size; // get file size 
          var fileType = RSFiles[i].type; // get file type
          profileRSName = fileName; 
        }
    }

      var raisedata = {
      ID:Number($("#Raiseform #raiseid").val()),
      MTUSER_ID:$("#mtuser_id").val(),
      EMAIL:$("#mtuser_email").val(),
      MODULE:$("#mtuser_module").val(),
      RAISE_NAME:$("#Raiseform #rsName").val(),
      RAISE_EMAIL:$("#Raiseform #rsemail").val(),
      RAISE_FOUNDER_URL1:$("#Raiseform #rsfounderurl1").val(),
      RAISE_FOUNDER_URL2:$("#Raiseform #rsfounderurl2").val(),
      RAISE_COMPANY_NAME:$("#Raiseform #rsRegCompanyname").val(),
