import React from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image';
import Button from '../elements/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonGroup from '../elements/ButtonGroup';
import classNames from 'classnames';


  
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
 



class Company_Info extends React.Component {
    constructor() {
    super();
    this.state = {
      input: {},
      errors: {},
      ID: null,
      MTUSER_ID:null,
      EMAIL:"",
      MODULE:"",
      COUNTRY:"",
      STATE:"",
      CITY:"",
      PINCODE:"",
      ADDRESS:"",
      COMPANY_WEBSITE:"",
      FB_LINK:"",
      INSTA_LINK:"",
      LINKEDIN_LINK:"",
      LEGAL_NAME:"",
      CIN_NUMBER:"",
      DATE_OF_INCORPORATATION:"",
      ABOUT_COMPANY:"",
      ABOUT_INVESTED:"",
      NO_OF_EMPLOYEES:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.saveCompanyInfo = this.saveCompanyInfo.bind(this);
    this.newCompanyInfo = this.newCompanyInfo.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
     
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;
  
    console.log(document.getElementById("mtuser_id").value);

    this.setState({
      // ID: null,
      MTUSER_ID:document.getElementById("mtuser_id").value,
      EMAIL:document.getElementById("mtuser_email").value,
      MODULE:document.getElementById("mtuser_module").value,
      COUNTRY:this.state.input.country,
      STATE:this.state.input.state,
      CITY:this.state.input.city,
      PINCODE:this.state.input.pincode,
      ADDRESS:this.state.input.address,
      COMPANY_WEBSITE:this.state.input.CompanyWebsite,
      FB_LINK:this.state.input.FacebookLink,
      INSTA_LINK:this.state.input.InstagramLink,
      LINKEDIN_LINK:this.state.input.LinkedInLink,
      LEGAL_NAME:this.state.input.LegalName,
      CIN_NUMBER:this.state.input.CinNumber,
      DATE_OF_INCORPORATATION:this.state.input.Date,
      ABOUT_COMPANY:this.state.input.Sector,
      ABOUT_INVESTED:this.state.input.AmountInvested,
      NO_OF_EMPLOYEES:this.state.input.Employees,
      STATUS:"Active",
      COMMENTS:"TEST",
      DESCRIPTION:"TEST",
      CREATED_USER:document.getElementById("mtuser_fname").value,
      CREATED_DATE:date,
      MODIFIED_USER:document.getElementById("mtuser_fname").value,
      MODIFIED_DATE:date
    });
  }
     
  handleSubmit(event) {
    event.preventDefault();
  
    if(this.validate()){
        console.log(this.state);
  
        let input = {};
        input["country"] = "";
        input["state"] = "";
        input["city"] = "";
        input["pincode"] = "";
        input["address"] = "";
        input["CompanyWebsite"] = "";
        input["FacebookLink"] = "";
        input["InstagramLink"] = "";
        input["LinkedInLink"] = "";
        input["legalName"] = "";
        input["CinNumber"] = "";
        input["Incorporation"] = "";
        input["Sector"] = "";
        input["AmountInvested"] = "";
        input["Employees"] = "";
        input["Date"] = "";
        
        this.setState({input:input});
  
        alert('Companyinfo Form is submitted');
    }
  }
  
  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["country"]) {
        isValid = false;
        errors["country"] = "Please enter your country.";
      }
  
      if (typeof input["country"] !== "undefined") {
        const re = /^\S*$/;
        if(input["country"].length < 2 || !re.test(input["country"])){
            isValid = false;
            errors["country"] = "Please enter valid country.";
        }
      }
  
      if (!input["state"]) {
        isValid = false;
        errors["state"] = "Please enter your state.";
      }
  
      if (typeof input["state"] !== "undefined") {
        const re = /^\S*$/;
        if(input["state"].length < 3 || !re.test(input["state"])){
            isValid = false;
            errors["state"] = "Please enter valid state.";
        }
      }
      

      if (!input["city"]) {
        isValid = false;
        errors["city"] = "Please enter your city.";
      }
  
      if (typeof input["city"] !== "undefined") {
        const re = /^\S*$/;
        if(input["city"].length < 3 || !re.test(input["city"])){
            isValid = false;
            errors["city"] = "Please enter valid city.";
        }
      }

      if (!input["pincode"]) {
        isValid = false;
        errors["pincode"] = "Please enter your pincode.";
      }
  
      if (typeof input["pincode"] !== "undefined") {
        const re = /^\S*$/;
        if(input["pincode"].length < 6 || !re.test(input["pincode"])){
            isValid = false;
            errors["pincode"] = "Please enter valid pincode.";
        }
      }

      if (!input["address"]) {
        isValid = false;
        errors["address"] = "Please enter your address.";
      }
  
      if (typeof input["address"] !== "undefined") {
        const re = /^\S*$/;
        if(input["address"].length < 6 || !re.test(input["address"])){
            isValid = false;
            errors["address"] = "Please enter valid address.";
        }
      }

      if (!input["CompanyWebsite"]) {
        isValid = false;
        errors["CompanyWebsite"] = "Please enter your Company website.";
      }
  
      if (typeof input["CompanyWebsite"] !== "undefined") {
        const re = /^\S*$/;
        if(input["CompanyWebsite"].length < 6 || !re.test(input["CompanyWebsite"])){
            isValid = false;
            errors["CompanyWebsite"] = "Please enter your Company Website.";
        }
      }

      if (!input["FacebookLink"]) {
        isValid = false;
        errors["FacebookLink"] = "Please enter your Facebook Link.";
      }
  
      if (typeof input["FacebookLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["FacebookLink"].length < 6 || !re.test(input["FacebookLink"])){
            isValid = false;
            errors["FacebookLink"] = "Please enter your Facebook Link.";
        }
      }

      if (!input["InstagramLink"]) {
        isValid = false;
        errors["InstagramLink"] = "Please enter your Instagram Link.";
      }
  
      if (typeof input["InstagramLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["InstagramLink"].length < 6 || !re.test(input["InstagramLink"])){
            isValid = false;
            errors["InstagramLink"] = "Please enter your Instagram Link.";
        }
      }

      if (!input["LinkedInLink"]) {
        isValid = false;
        errors["LinkedInLink"] = "Please enter your Linked In Link.";
      }
  
      if (typeof input["LinkedInLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LinkedInLink"].length < 6 || !re.test(input["LinkedInLink"])){
            isValid = false;
            errors["LinkedInLink"] = "Please enter your Linked In Link.";
        }
      }

      if (!input["LegalName"]) {
        isValid = false;
        errors["LegalName"] = "Please enter your Legal Name.";
      }
  
      if (typeof input["LegalName"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LegalName"].length < 6 || !re.test(input["LegalName"])){
            isValid = false;
            errors["LegalName"] = "Please enter your Legal Name.";
        }
      }

      if (!input["CinNumber"]) {
        isValid = false;
        errors["CinNumber"] = "Please enter your CIN Number.";
      }
  
      if (typeof input["CinNumber"] !== "undefined") {
        const re = /^\S*$/;
        if(input["CinNumber"].length < 6 || !re.test(input["CinNumber"])){
            isValid = false;
            errors["CinNumber"] = "Please enter your CIN Number.";
        }
      }

      if (!input["Date"]) {
        isValid = false;
        errors["Date"] = "Please enter Date of Incorporation.";
      }

      if (!input["Incorporation"]) {
        isValid = false;
        errors["Incorporation"] = "Please enter your Incorporation Type.";
      }
  
      if (typeof input["Incorporation"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Incorporation"].length < 6 || !re.test(input["Incorporation"])){
            isValid = false;
            errors["Incorporation"] = "Please enter your Incorporation Type.";
        }
      }

      if (!input["Sector"]) {
        isValid = false;
        errors["Sector"] = "Please enter Sector.";
      }
  
      if (typeof input["Sector"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Sector"].length < 3 || !re.test(input["Sector"])){
            isValid = false;
            errors["Sector"] = "Please enter Sector.";
        }
      }


      if (!input["AmountInvested"]) {
        isValid = false;
        errors["AmountInvested"] = "Enter Amount Invested in Business till date.";
      }
  
      if (typeof input["AmountInvested"] !== "undefined") {
        const re = /^\S*$/;
        if(input["AmountInvested"].length < 2 || !re.test(input["AmountInvested"])){
            isValid = false;
            errors["AmountInvested"] = "Enter Amount Invested in Business till date.";
        }
      }


      if (!input["Employees"]) {
        isValid = false;
        errors["Employees"] = "Enter No. of Employees.";
      }
  
      if (typeof input["Employees"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Employees"].length < 1 || !re.test(input["Employees"])){
            isValid = false;
            errors["Employees"] = "Enter No. of Employees.";
        }
      }




      
      
      
  
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
  render() {
    return (
        <>
        <body className='bg-white' >
            <div className='container'>
                       
                <div className='row' style={{height:'auto',marginTop:100}}>
                    <div className='row'>
                      <div className="hero-content">
                         <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item ">
                                          <NavLink to="/Startup_Dashboard" className="nav-link arrow-right"
                                           style={{color:"#B1B0AD"}}> Back to Dashboard</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
