import React, {useState} from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09


  


class Team_Info extends React.Component {
    constructor() {
    super();
    this.state = {
      
      teaminfoAllDets: [],
      currentteaminfo: null,
      currentteaminfoIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      TEAM_SNO:"",
      TEAM_MEMBER_NAME:"",
      TEAM_MEMBER_POSITION:"",
      FB_LINK:"",
      INSTA_LINK:"",
      LINKEDIN_LINK:"",
      TEAM_BIO:"",
      PROFILE_PIC:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.saveTeamInfo = this.saveTeamInfo.bind(this);  
    this.retrieveAllTeamInfo = this.retrieveAllTeamInfo.bind(this);
    this.setActiveTeaminfo = this.setActiveTeaminfo.bind(this);
    
  }
     
  componentDidMount() {
    this.retrieveAllTeamInfo();
    // console.log(formatDate)
  }

  retrieveAllTeamInfo() {
    // console.log(sessionStorage.getItem("sessEmail"));
    var querySet = "/Teaminfo?EMAIL="+sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
     .then(response => {
       this.setState({
        teaminfoAllDets: response.data, 
       });
       
        this.state.teaminfoAllDets.map((tinfo, index) => (
            $("#teaminfoid").attr("value",tinfo.ID),
            $("#tmsno").attr("value",tinfo.TEAM_SNO),
            $("#tmname").attr("value",tinfo.TEAM_MEMBER_NAME),
            $("#tmposition").attr("value",tinfo.TEAM_MEMBER_POSITION),
            $("#tmfblink").attr("value",tinfo.FB_LINK),
            $("#tminstalink").attr("value",tinfo.INSTA_LINK),
            $("#tmlinklink").attr("value",tinfo.LINKEDIN_LINK),
            $("#tmteambio").attr("value",tinfo.TEAM_BIO)
            // $("#tmprofileinfo").attr("value",cinfo.PROFILE_PIC)
            
          ));
       
     })
     .catch(e => {
       console.log(e);
     });
 }


 setActiveTeaminfo(teaminfoAllDets, index) {
  this.setState({
    currentteaminfo: teaminfoAllDets,
    currentteaminfoIndex: index
  });
} 
     
 
saveTeamInfo(event) {
  event.preventDefault();  
