validateTeamInfo(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (window.isEmpty($("#tmname").val())) {
        isValid = false;
        errors["tmname"] = "Please enter your Team Member Name.";
      } 
  
      if (window.isEmpty($("#tmposition").val())) {
        isValid = false;
        errors["tmposition"] = "Please enter Position Name.";
      }
   
      if (window.isEmpty($("#tmfblink").val())) {
        isValid = false;
        errors["tmfblink"] = "Please enter your Facebook Link.";
      }
   
      if (window.isEmpty($("#tminstalink").val())) {
        isValid = false;
        errors["tminstalink"] = "Please enter your Instagram Link.";
      }
   
      if (window.isEmpty($("#tmlinklink").val())) {
        isValid = false;
