import React, {Component} from "react"; 
import Button from '../elements/Button'; 
import Image from "../elements/Image"; 
import '../Css/styles.css';
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import ButtonGroup from "../elements/ButtonGroup"; 
import {NavLink,Link} from "react-router-dom"; 
import DataService from '../../service/DataService'; 
import UploadService from "../../service/file-upload.service";
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09





class Create_Pitch extends React.Component {
    constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };
     
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
     
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;
  
    this.setState({
      input
    });
  }
     
  handleSubmit(event) {
    event.preventDefault();
  
    if(this.validate()){
        console.log(this.state);
  
        let input = {};
        input["header"] = "";
        input["summarnote"] = "";
        
        
        
        this.setState({input:input});
  
        alert('Teaminfo Form is submitted');
    }
  }
  
  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["header"]) {
        isValid = false;
        errors["header"] = "Enter Your header/title here.";
      }
  
      if (typeof input["header"] !== "undefined") {
        const re = /^\S*$/;
        if(input["header"].length < 4 || !re.test(input["header"])){
            isValid = false;
            errors["header"] = "Enter Your header/title here.";
        }
      }

      if (!input["summarnote"]) {
        isValid = false;
        errors["summarnote"] = "Enter body.";
      }
  
      if (typeof input["summarnote"] !== "undefined") {
        const re = /^\S*$/;
        if(input["summarnote"].length < 3 || !re.test(input["summarnote"])){
            isValid = false;
            errors["summarnote"] = "Enter body.";
        }
      }





    
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
 render() {
    
 

return (

  <body className='' >
  <div className='container'>  
      <div className='row' style={{height:'auto',marginTop:100}}>
          <div className='row'>
            <div className="hero-content">
               <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                  <div className="container-fluid"  >
                        <ul className="navbar-nav" style={{fontSize:13}}> 
                            <li className="nav-item">
                                <NavLink to="/Startup_Dashboard" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>&nbsp;Back to Dashboard</NavLink>
                              </li>
                        
                          </ul>
                    </div>
                  </nav>

                 <div className='container bg-white p-5'>

                  
                 <div className='row'> 


                <div className='col-md-1'> <a href='/Create_Pitch'><Image   src={require('./../../assets/images/p1.png')}alt="Features tile icon 01"  width={50} height={50} borderRadius={50} /><p style={{fontSize:13}}>Problem</p></a> </div>
                   <div className='col-md-1'> <a href='/Solution'><Image   src={require('./../../assets/images/p002.png')}alt="Features tile icon 01"  width={50} height={50} /><p style={{fontSize:13,color:"#2ECC71"}}>Solution</p></a> </div>
                   <div className='col-md-1'> <a href='/Product'><Image   src={require('./../../assets/images/p003.png')}alt="Features tile icon 01"  width={50} height={50} /><p style={{fontSize:13}}>Product</p></a> </div>
                   <div className='col-md-1'> <a href='/Transaction'><Image   src={require('./../../assets/images/p4.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Transaction</p></a> </div> 
                   <div className='col-md-1'> <a href='/Business_Model'><Image   src={require('./../../assets/images/p5.webp')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Business<br/>Model</p></a> </div>
                   <div className='col-md-1'> <a href='/Competition'><Image   src={require('./../../assets/images/p6.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Competition</p></a> </div>
                   <div className='col-md-1'> <a href='/Customer'><Image   src={require('./../../assets/images/p7.jpg')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Customers</p></a> </div>
                   <div className='col-md-1'> <a href='/Usage'><Image   src={require('./../../assets/images/p8.jpg')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Usage of<br/>Funds</p></a> </div>
                   <div className='col-md-1'> <a href='/Vision'><Image   src={require('./../../assets/images/900.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Vision</p></a> </div>
                   <div className='col-md-1'> <a href='/Potential_Returns'><Image   src={require('./../../assets/images/p10.webp')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Potential<br/>Reutrns</p></a> </div>
                  </div> 
                  
                    <div className='row' style={{height:'auto'}}>
                    <div className='col-md-12'>
                                        <h3>Create Pitch</h3>


                                        <form name="TeamInfoform" id="TeamInfoform" method="POST" enctype="multipart/form-data" className="row m-5 g-3"  >  

<div className="col-md-12"  > 
  
    <div className="col-md-6 mb-3 mt-3">
    <label for="tmname" className="form-label">Name</label>
    <input name="teaminfoid" id="teaminfoid" type="hidden"  />
     
    <input
                              name="header" 
                              type="text" 
                              value={this.state.input.header}
                              onChange={this.handleChange} 
                              className="form-control" 
                              placeholder="Enter Your header/title here"
                              length="50" 
                              id="header"/>
                         
          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmname}</div>
    </div> 

    
    <div className="col-md-8 mb-3 mt-3" >
    <label for="tmposition" className="form-label">Position in the Company</label>
    
    <Editor  
                      toolbarClassName="toolbarClassName"
                      wrapperClassName="wrapperClassName"
                      editorClassName="editorClassName"
                     name="summarnote"
                      value={this.state.input.summarnote}
                      onChange={this.handleChange}
                      id="summarnote" 
                    />
          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmposition}</div>
    </div> 


<div className="col-md-6 mb-3 mt-3"> 
<div className="input-group "><Image   src={require('./../../assets/images/face.png')}alt="Features tile icon 01"  width={50} height={50}/>
    <input
         name='tmfblink' 
         value={this.state.input.tmfblink}
         onChange={this.handleChange}
         type="text" 
         className="form-control"  
         maxlength="150"  
         placeholder="Facebook Link"
         id='tmfblink'/>
            </div>
            <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmfblink}</div>
      
    </div>
     

    <div className="col-md-6 mb-3 mt-3">
    <div className="input-group ">
      <Image   src={require('./../../assets/images/insta.jpg')}alt="Features tile icon 01"  width={50} height={50}/> 
    <input
         name='tminstalink' 
         value={this.state.input.tminstalink}
         onChange={this.handleChange}
         type="text" 
         className="form-control"  
         maxlength="150"  
         placeholder="Instagram Link"
         id='tminstalink'/>
         
         </div>
             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tminstalink}</div>
    </div> 

    <div className="col-md-6 mb-3 mt-3">
    <div className="input-group ">
      <Image   src={require('./../../assets/images/lin.png')}alt="Features tile icon 01"  width={50} height={50}/> 
    <input
          name='tmlinklink' 
          value={this.state.input.tmlinklink}
          onChange={this.handleChange}
          type="text" 
          className="form-control"  
          maxlength="150"  
          placeholder="LinkedIn Link"
          id='tmlinklink'/>
          </div>
          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmlinklink}</div>
      
    </div>
   
    <div className="col-md-6 mb-3 mt-3">
  <label for="tmteambio" className="form-label">Bio</label>
  
    <textarea  name='tmteambio' 
         value={this.state.input.bio}
          onChange={this.handleChange}
         type="text" 
         className="form-control"  
         maxlength="250"  
         rows="10"
         placeholder="Enter Your Bio" 
         id='tmteambio'></textarea> 
     <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmteambio}</div>
      
    </div>

  <div className="col-md-6 mb-3 mt-3">
    <div className="row mb-3">
    <div className="col-md-6">
    <label for="tmuploadprofile" className="col-sm-2 form-label">Profile</label>
    </div>

    <div className="col-md-6 imgUp">    
              <div className="imagePreview"></div>
          <label   className="col-md-12 btn btn-outline-secondary btn-sm">
                Upload<br/>
                <input type="file" id="tmuploadprofile" name="tmuploadprofile" 
                accept="image/*" data-attr="1" className=" uploadFile img"  multiple 
                style={{height:"0px",width:"0px",overflow : "hidden"}}/>
                  </label>
                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmuploadprofile}</div>
            </div>   

    </div>
  </div>
     
 </div>
 <div className="col-md-12">
 <div className="row">
<div className="col-md-3 mb-3" >&nbsp;
{/* <button type="button" className="btn btn-outline-secondary btn-sm w-100" data-attr-id="teamInfoDivisionIter" id="teaminfoAddmem">Add Members</button> */}
</div>
<div className="col-md-3 mb-3" >
<button type="button" className="btn btn-success btn-sm  w-100"  onClick={this.saveTeamInfo}>Next</button>
</div>


</div>
 </div>


</form>




                                        </div>

                    </div></div>
    
                    </div>
                    
                    </div>
                    
                    
                    </div></div> 
 

    </body>          
              
              

              


              
               
               

) ;       
 }
};



export default Create_Pitch;
