import React from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image';
import Button from '../elements/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonGroup from '../elements/ButtonGroup';
import classNames from 'classnames';
 

class Raise extends React.Component {
  constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };
     
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
     
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;
  
    this.setState({
      input
    });
  }
     
  handleSubmit(event) {
    event.preventDefault();
  
    if(this.validate()){
        console.log(this.state);
  
        let input = {};
        input["name"] = "";
        input["email"] = "";
        input["linkedinurl"] = "";
        input["companyname"] = "";
        input["linkedinpage"] = "";
        input["website"] = "";
        input["fundraising"] = "";
        input["product"] = "";
        input["traction"] = "";
        input["revenue"] = "";
        input["team"] = "";
        input["community"] = "";
        input["rightfit"] = "";
        input["commitments"] = "";
        input["privateround"] = "";
        input["pitch"] = "";
        this.setState({input:input});
  
        alert('Registration Form is submitted');
    }
  }
  
  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["name"]) {
        isValid = false;
        errors["name"] = "Please enter your name.";
      }
  
      if (typeof input["name"] !== "undefined") {
        const re = /^\S*$/;
        if(input["name"].length < 6 || !re.test(input["name"])){
            isValid = false;
            errors["name"] = "Please enter valid name.";
        }
      }

      if (!input["email"]) {
        isValid = false;
        errors["email"] = "Please enter your Conpany email Address.";
      }
  
      if (typeof input["email"] !== "undefined") {
          
        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(input["email"])) {
          isValid = false;
          errors["email"] = "Please enter valid Company email address.";
        }
      }

      if (!input["linkedinurl"]) {
        isValid = false;
        errors["linkedinurl"] = "Please enter your Founder's LinkedIn URL.";
      }
  
      if (typeof input["linkedinurl"] !== "undefined") {
        const re = /^\S*$/;
        if(input["linkedinurl"].length < 2 || !re.test(input["linkedinurl"])){
            isValid = false;
            errors["linkedinurl"] = "Please enter valid Founder's LinkedIn URL.";
        }
      }

      if (!input["companyname"]) {
        isValid = false;
        errors["companyname"] = "Please enter your Registered Company Name.";
      }
  
      if (typeof input["companyname"] !== "undefined") {
        const re = /^\S*$/;
        if(input["companyname"].length < 2 || !re.test(input["companyname"])){
            isValid = false;
            errors["companyname"] = "Please enter valid Registered Company Name.";
        }
      }

      if (!input["linkedinpage"]) {
        isValid = false;
        errors["linkedinpage"] = "Please enter your Company's LinkedIn Page.";
      }
  
      if (typeof input["linkedinpage"] !== "undefined") {
        const re = /^\S*$/;
        if(input["linkedinpage"].length < 2 || !re.test(input["linkedinurl"])){
            isValid = false;
            errors["linkedinpage"] = "Please enter valid Company's LinkedIn Page.";
        }
      }

      if (!input["website"]) {
        isValid = false;
        errors["website"] = "Please enter your Company Website.";
      }
  
      if (typeof input["website"] !== "undefined") {
        const re = /^\S*$/;
        if(input["website"].length < 2 || !re.test(input["website"])){
            isValid = false;
            errors["website"] = "Please enter valid Company Website.";
        }
      }

      if (!input["fundraising"]) {
        isValid = false;
        errors["fundraising"] = "Please Describe your previous fundraising rounds.";
      }
  
      if (typeof input["fundraising"] !== "undefined") {
        const re = /^\S*$/;
        if(input["fundraising"].length < 2 || !re.test(input["fundraising"])){
            isValid = false;
            errors["fundraising"] = "Please Describe your previous fundraising rounds.";
        }
      }

      if (!input["product"]) {
        isValid = false;
        errors["product"] = "Please Describe your product.";
      }
  
      if (typeof input["product"] !== "undefined") {
        const re = /^\S*$/;
        if(input["product"].length < 2 || !re.test(input["product"])){
            isValid = false;
            errors["product"] = "Please Describe your product.";
        }
      }

      if (!input["traction"]) {
        isValid = false;
        errors["traction"] = "Please Describe the traction.";
      }
  
      if (typeof input["traction"] !== "undefined") {
        const re = /^\S*$/;
        if(input["traction"].length < 2 || !re.test(input["linkedinurl"])){
            isValid = false;
            errors["traction"] = "Please Describe the traction.";
        }
      }

      if (!input["revenue"]) {
        isValid = false;
        errors["revenue"] = "Please Describe the revenue you are making.";
      }
  
      if (typeof input["revenue"] !== "undefined") {
        const re = /^\S*$/;
        if(input["revenue"].length < 2 || !re.test(input["revenue"])){
            isValid = false;
            errors["revenue"] = "Please Describe the revenue you are making.";
        }
      }

      if (!input["team"]) {
        isValid = false;
        errors["team"] = "Please enter your Current Team Size.";
      }
  
      if (typeof input["team"] !== "undefined") {
        const re = /^\S*$/;
        if(input["team"].length < 2 || !re.test(input["team"])){
            isValid = false;
            errors["team"] = "Please enter Current Team Size.";
        }
      }

      if (!input["community"]) {
        isValid = false;
        errors["community"] = "Please enter Why do you want to raise a Community round?";
      }
  
      if (typeof input["community"] !== "undefined") {
        const re = /^\S*$/;
        if(input["community"].length < 2 || !re.test(input["community"])){
            isValid = false;
            errors["community"] = "Please enter Why do you want to raise a Community round?";
        }
      }

      if (!input["rightfit"]) {
        isValid = false;
        errors["rightfit"] = "Please enter What makes you think Myntinvest is the right fit for you?";
      }
  
      if (typeof input["rightfit"] !== "undefined") {
        const re = /^\S*$/;
        if(input["rightfit"].length < 2 || !re.test(input["rightfit"])){
            isValid = false;
            errors["rightfit"] = "Please enter What makes you think Myntinvest is the right fit for you?";
        }
      }

      
      if (!input["commitments"]) {
        isValid = false;
        errors["commitments"] = "Please enter Do you have any existing commitments?";
      }
  
      if (typeof input["commitments"] !== "undefined") {
        const re = /^\S*$/;
        if(input["commitments"].length < 2 || !re.test(input["commitments"])){
            isValid = false;
            errors["commitments"] = "Please enter Do you have any existing commitments?.";
        }
      }

      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
  render() {
    return (
      <>
      <body className='bg-white' >
          <div className='container'>
                     
              <div className='row' style={{height:'auto',marginTop:100}}>
                  <div className='row'>
                    <div className="hero-content">
                       

                         <div className='container'>
                         <form name="Raiseform" id="Raiseform" method="POST" className="row m-5 g-3"  > 
                         
  
                         <div className='row' style={{height:'auto'}}>
                                  <div className='col-md-12'>
                                      <h1>Raise with Tyke</h1>
                                      <p className="font20">
                                        <b>Tell us a little about your company. 
                                          This will help us understand your business better.</b></p>
                                      <div className="row m-1">
                          <ul className="nav nav-tabs">
<li className="nav-item">
  <a className="para" data-bs-toggle="tab" href="#raisecapital"> 
  <b className='lightTxt '>Application</b></a>
</li>

</ul>

<div className="tab-content">
<div className="tab-pane container active" id="raisecapital">
{/* <p className='para'>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Tincidunt libero vel elementum at cum tupis eget. Viverra ultrices lacus, lectus volutpat sociis vitae mauris<br/>porta faugiat. Nec, vitae facilisi elementum eu est vel quis platea. Diam pharetra nec malesuada mi purus erat.</p> */}
<div className="col-md-6 mb-3 mt-3">
  <label for="rsName" className="form-label">Your Name<span className='maroon'>*</span></label>
  <input 
     name="raiseid"  
     id="raiseid" 
     type="hidden"
  />
  <input  name="rsName" 
        value={this.state.input.rsName}
        onChange={this.handleChange}
        type="text" 
        className="form-control"  
        maxlength="150"  
        placeholder="Enter Your Name"
        id="rsName"
         />
           <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rsName}</div>
          
</div>
<div className="col-md-6 mb-3">
  <label for="rsemail" className="form-label">Company Email<span className='maroon'>*</span></label>
  <input 
          name="rsemail" 
          value={this.state.input.rsemail}
          onChange={this.handleChange}                                            
          type="text" 
          className="form-control"  
          maxlength="150"  
          placeholder="Enter Your Company Email"
          id="rsemail"
          />
             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rsemail}</div>
</div>   




<div className="col-md-6 mb-3"  >   
<label for="rsfounderurl1" className="form-label">Founder's LinkedIn URL<span className='maroon'>*</span></label>
                                          
          <input 
          name="rsfounderurl1"
          value={this.state.input.rsfounderurl1}
          onChange={this.handleChange}
          type="text" 
          className="form-control url_link"  
          maxlength="250"  
          placeholder="Founder's LinkedIn URL 1"
          id='rsfounderurl1'
          />
             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rsfounderurl1}</div>
    </div>


    <div className="col-md-6 mb-3"  >   
<label for="rsfounderurl2" className="form-label">Founder's LinkedIn URL<span className='maroon'>*</span></label>
                                          
          <input 
          name="rsfounderurl2"
          value={this.state.input.rsfounderurl2}
          onChange={this.handleChange}
          type="text" 
          className="form-control  url_link"   
          maxlength="250"  
          placeholder="Founder's LinkedIn URL 2"
          id='rsfounderurl2'
          />
             <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rsfounderurl2}</div>
    </div>

    <div className="col-md-6 mb-3"  >
            <label for="rsRegCompanyname" className="form-label">Registered Company Name</label>
                  <input 
                  name='rsRegCompanyname'
                  value={this.state.input.rsRegCompanyname}
                  onChange={this.handleChange}
                  type="text" 
                  className="form-control "  
                  maxlength="250"  
                  placeholder="Enter Your company's registered name"
                  id='rsRegCompanyname'
                  />
                   <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rsRegCompanyname}</div>
            </div>

  <div className="col-md-6 mb-3" >
<label for="rscomLinkpage" className="form-label">Company's LinkedIn Page<span className='maroon'>*</span></label>
<input 
  name='rscomLinkpage'
  value={this.state.input.rscomLinkpage}
  onChange={this.handleChange}
  type="text" 
  className="form-control"  
  maxlength="250"  
  placeholder="Enter Your Company's LinkedIn Page"
  id='rscomLinkpage'/>
   <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.rscomLinkpage}</div>
