import React, { useState, setState } from "react";
import "../Css/styles.css";
import ButtonGroup from "../elements/ButtonGroup";
import Button from "../elements/Button";
import Image from "../elements/Image";
import classNames from "classnames";
import BoyImage from "../../assets/da_img/login1.png";
import Google from "../../assets/images/google.png";
import DataService from "../../service/DataService";
import Moment from "moment";
import $ from "jquery";







const tilesClasses = classNames("tiles-wrap center-content");
class Welcome extends React.Component {
  constructor() {
    super();
    this.state = {
      invSignAllDetails: [],
      input: {},
      errors: {},
      EMAIL: "",
      PASSWORD: "",
      MESSAGE: "",
    };

    this.handleLogChange = this.handleLogChange.bind(this);
    this.validateLogin = this.validateLogin.bind(this);
    this.openAdminPanel = this.openAdminPanel.bind(this);
  }

  handleLogChange(event) {
    event.preventDefault();
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      EMAIL: this.state.input.logemail,
      PASSWORD: this.state.input.logpassword,
    });
  }
  openAdminPanel(event) {
    event.preventDefault();
    window.showAlert(
      "Welcome to Admin Panel",
      window.mt_backend_url + "/admin"
    );
  }

  validateLogin(event) {
    event.preventDefault();

    var loginstatus = false;
    //alert("status======loginstatus====>>>>>>>>>>>>>" + loginstatus);
    var data = {
      EMAIL: this.state.input.logemail,
      PASSWORD: this.state.input.logpassword,
    };

    var querySet1 = "/inv_nationality?EMAIL=" + $("#logemail").val();
    console.log("loginstatus=>" + querySet1);
    DataService.findByTitle(querySet1).then((response) => {
      this.setState({
        invSignAllDetails: response.data,
      });

      $.each(this.state.invSignAllDetails, function (index, value) {
        // alert(value.NATIONALITY);
        if (value.EMAIL == $("#logemail").val()) {
          if (!window.isEmpty(value.NATIONALITY)) {
            loginstatus = true;
          }
        }
      });
    });

    //alert("status======loginstatus====>>>>>>>>>>>>>" + loginstatus);

    DataService.passData(
      "/userlogin?EMAIL=" +
        this.state.input.logemail +
        "&PASSWORD=" +
        this.state.input.logpassword
    )
      .then((response) => {
        window.showLoader();
        sessionStorage.setItem("sessFirstname", response.data.FIRSTNAME);
        sessionStorage.setItem("sessEmail", response.data.EMAIL);
        sessionStorage.setItem("sessRole", response.data.ROLE);
        sessionStorage.setItem("sessModule", response.data.MODULE);
        sessionStorage.setItem("sessUserId", response.data.MT_USERID);

        window.sessionSetting(sessionStorage);
        if (response.data.ROLE == "User") {
          // window.showAlert("Welcome " + response.data.FIRSTNAME, "/Investors");
          if (loginstatus == true) {
            window.location.href = "/Investors_Dashboard"; //to redirect to normal links
          }

          if (loginstatus == false) {
            window.location.href = "/Investor_Signup"; //to redirect to normal links
          }
        } else if (response.data.ROLE == "Admin") {
          // window.showAlert("Welcome To Administrator", "/Admin_Dashboard");
          window.location.href = window.mt_backend_url + "/admin"; //to redirect to normal links
        }
      })
      .catch((e) => {
        window.showLoader();
        // window.showAlert("Username or Password is incorrect", "/Login");
        window.errorMessage(
          "Username or Password is incorrect",
          "loginmessage"
        );
        // window.location.href = "/Login";//to redirect to normal links
      });
  }

  validate() {
    let input = this.state.input;
    let errors = {};
    let isValid = true;

    if (!input["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input["email"] !== "undefined") {
      var pattern = new RegExp(
        /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
      );
      if (!pattern.test(input["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }

    if (!input["password"]) {
      isValid = false;
      errors["password"] = "Please enter your password.";
    }

    if (!input["confirm_password"]) {
      isValid = false;
      errors["confirm_password"] = "Please enter your confirm password.";
    }

    this.setState({
      errors: errors,
    });

    return isValid;
  }

  render() {
    return (
 
      <body className="bg_dtheme">
        <div class="container">
          <div class="row" style="background-color: #2d1621;">
           <div class="col-md-6"><img src={require("./../../assets/da_img/login1.png")} class="img-fluid" alt="..."/>
           </div>
             <div class="col-md-6">
              <img src={require("./../../assets/da_img/logo1.png")}  class="rounded mx-auto d-block" alt="..."/>
              <br/>
              <p class="fs-4 fst-italic text-white-50 text-center">Let's Do Aram</p>
              <div class="d-grid gap-2 col-8 mx-auto">
              <button  type="button"  className="btn button-white button-shadow btnG btn-sm w-100"
                  wideMobile  id="authorize_button" onclick="handleAuthClick()" >  
                  <span className="googleIcon"></span>
                  <p>&nbsp;Sign up with Google</p>
                </button>
              </div> 
              <form class="form-floating d-grid gap-2 col-8 mx-auto">
          
          <input type="email" class="form-control" id="floatingInputValue" placeholder="name@example.com"  />
          <label for="floatingInputValue">Email</label>
        </form>
 
        <form class="form-floating d-grid gap-2 col-8 mx-auto">
          <input type="text" class="form-control" id="floatingInputValue" placeholder="name@example.com"  />
          <label for="floatingInputValue">Password</label>
        </form>
        <div class="form-check mt-4 col-8 mx-auto">
         <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
         <label class="form-check-label link-light" for="flexCheckDefault">Remember me
         </label>
         <p class="text-end"><a href="#" class="text-decoration-none link-light text-end">Forgot password?</a></p>
        </div>
        
              <div class="d-grid gap-2 col-3 mx-auto">
                <button type="button" class="btn btn-outline-light fw-bold">Log in</button>
                </div> 
                <p class="text-center link-light">Not a member yet?
                <a href="#" class="text-rest text-warning text-center">Sign Up Now</a></p>
                </div>
          </div>
        </div>
      </body>
    );
  }
}

export default Welcome;
