import React, {useState} from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image';
import Button from '../elements/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonGroup from '../elements/ButtonGroup';
import $ from "jquery";

  


class Team_Info extends React.Component {
    constructor() {
    super();
    this.state = {
      
      teaminfoAllDets: [],
      currentteaminfo: null,
      currentteaminfoIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      TEAM_SNO:"",
      TEAM_MEMBER_NAME:"",
      TEAM_MEMBER_POSITION:"",
      FB_LINK:"",
      INSTA_LINK:"",
      LINKEDIN_LINK:"",
      TEAM_BIO:"",
      PROFILE_PIC:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.saveTeamInfo = this.saveCompanyInfo.bind(this);  
    this.retrieveAllCompanyInfo = this.retrieveAllCompanyInfo.bind(this);
    this.setActiveCinfo = this.setActiveCinfo.bind(this);
    
  }
     
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;
  
    this.setState({
      input
    });
  }
     
  handleSubmit(event) {
    event.preventDefault();
  
    if(this.validate()){
        console.log(this.state);
  
        let input = {};
        input["teammember"] = "";
        input["position"] = "";
        input["FacebookLink"] = "";
        input["InstagramLink"] = "";
        input["LinkedInLink"] = "";
        input["bio"] = "";
        
        
        this.setState({input:input});
  
        alert('TeamInfo Form is submitted');
    }
  }
  
  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["teammember"]) {
        isValid = false;
        errors["teammember"] = "Please enter your Team Member Name.";
      }
  
      if (typeof input["teammember"] !== "undefined") {
        const re = /^\S*$/;
        if(input["teammember"].length < 2 || !re.test(input["teammember"])){
            isValid = false;
            errors["teammember"] = "Please enter your Team Member Name.";
        }
      }
  
      if (!input["position"]) {
        isValid = false;
        errors["position"] = "Please enter Position Name.";
      }
  
      if (typeof input["position"] !== "undefined") {
        const re = /^\S*$/;
        if(input["position"].length < 3 || !re.test(input["position"])){
            isValid = false;
            errors["position"] = "Please enter Position Name.";
        }
      }


      if (!input["FacebookLink"]) {
        isValid = false;
        errors["FacebookLink"] = "Please enter your Facebook Link.";
      }
  
      if (typeof input["FacebookLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["FacebookLink"].length < 5 || !re.test(input["FacebookLink"])){
            isValid = false;
            errors["FacebookLink"] = "Please enter your Facebook Link.";
        }
      }

      if (!input["InstagramLink"]) {
        isValid = false;
        errors["InstagramLink"] = "Please enter your Instagram Link.";
      }
  
      if (typeof input["InstagramLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["InstagramLink"].length < 5 || !re.test(input["InstagramLink"])){
            isValid = false;
            errors["InstagramLink"] = "Please enter your Instagram Link.";
        }
      }

      if (!input["LinkedInLink"]) {
        isValid = false;
        errors["LinkedInLink"] = "Please enter your Linked In Link.";
      }
  
      if (typeof input["LinkedInLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LinkedInLink"].length < 5 || !re.test(input["LinkedInLink"])){
            isValid = false;
            errors["LinkedInLink"] = "Please enter your Linked In Link.";
        }
      }



      if (!input["bio"]) {
        isValid = false;
        errors["bio"] = "Please Enter Your Company Website.";
      }
  
      if (typeof input["bio"] !== "undefined") {
        const re = /^\S*$/;
        if(input["bio"].length < 6 || !re.test(input["bio"])){
            isValid = false;
