import React, {useState} from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image';
import Button from '../elements/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonGroup from '../elements/ButtonGroup';


  


class Team_Info extends React.Component {
    constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };
     
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
     
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;
  
    this.setState({
      input
    });
  }
     
  handleSubmit(event) {
    event.preventDefault();
  
    if(this.validate()){
        console.log(this.state);
  
        let input = {};
        input["teammember"] = "";
        input["position"] = "";
        input["FacebookLink"] = "";
        input["InstagramLink"] = "";
        input["LinkedInLink"] = "";
        input["bio"] = "";
        
        
        this.setState({input:input});
  
        alert('TeamInfo Form is submitted');
    }
  }
  
  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["teammember"]) {
        isValid = false;
        errors["teammember"] = "Please enter your Team Member Name.";
      }
  
      if (typeof input["teammember"] !== "undefined") {
        const re = /^\S*$/;
        if(input["teammember"].length < 2 || !re.test(input["teammember"])){
            isValid = false;
            errors["teammember"] = "Please enter your Team Member Name.";
        }
      }
  
      if (!input["position"]) {
        isValid = false;
        errors["position"] = "Please enter Position Name.";
      }
  
      if (typeof input["position"] !== "undefined") {
        const re = /^\S*$/;
        if(input["position"].length < 3 || !re.test(input["position"])){
            isValid = false;
            errors["position"] = "Please enter Position Name.";
        }
      }


      if (!input["FacebookLink"]) {
        isValid = false;
        errors["FacebookLink"] = "Please enter your Facebook Link.";
      }
  
      if (typeof input["FacebookLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["FacebookLink"].length < 5 || !re.test(input["FacebookLink"])){
            isValid = false;
            errors["FacebookLink"] = "Please enter your Facebook Link.";
        }
      }

      if (!input["InstagramLink"]) {
        isValid = false;
        errors["InstagramLink"] = "Please enter your Instagram Link.";
      }
  
      if (typeof input["InstagramLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["InstagramLink"].length < 5 || !re.test(input["InstagramLink"])){
            isValid = false;
            errors["InstagramLink"] = "Please enter your Instagram Link.";
        }
      }

      if (!input["LinkedInLink"]) {
        isValid = false;
        errors["LinkedInLink"] = "Please enter your Linked In Link.";
      }
  
      if (typeof input["LinkedInLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LinkedInLink"].length < 5 || !re.test(input["LinkedInLink"])){
            isValid = false;
            errors["LinkedInLink"] = "Please enter your Linked In Link.";
        }
      }



      if (!input["bio"]) {
        isValid = false;
        errors["bio"] = "Please Enter Your Company Website.";
      }
  
      if (typeof input["bio"] !== "undefined") {
        const re = /^\S*$/;
        if(input["bio"].length < 6 || !re.test(input["bio"])){
            isValid = false;
            errors["bio"] = "Please Enter Your Company Website.";
        }
      }

      

     
      
  
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
 render() {

    return (
        <>
        <body className='' >
            <div className='container'>
                       
                <div className='row' style={{height:'auto',marginTop:100}}>
                    <div className='row'>
                      <div className="hero-content">
                         <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item">
                                          <NavLink to="/Startup_Dashboard" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>Back to Dashboard</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
                            </nav>

                           <div className='container'>
                              <div className='row' style={{height:'auto'}}>
                              
                                    <div className='col-md-12'>
                                        <h3>Team Info</h3>
                                        <p className='para'>
                                          Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                          Tincidunt libero vel elementum at cum tupis eget. Viverra ultrices lacus,
                                           lectus volutpat sociis vitae mauris<br/>porta faugiat. Nec, vitae facilisi 
                                           elementum eu est vel quis platea. Diam pharetra nec malesuada mi purus erat.</p>
                                    
                                        
                                        
                                     <div className='row'>
                                     <form onSubmit={this.handleSubmit}>
                                      


                                        <div className="col-md-12">
                                        <span className='spanbgcircle'>01</span> 
                                            
                                            <div class="col-md-6 mb-3 mt-3">
                                            <label for="teammember" class="form-label">Name</label>
                                            <input
                                                  name='teammember' 
                                                  type="text" 
                                                  value={this.state.input.teammember}
                                                  onChange={this.handleChange}
                                                  class="form-control"  
                                                  maxlength="15"  
                                                  placeholder="Enter Your Team Member Name" 
                                                  id='teammember'/>
                                            </div> 

                                            
                                            <div class="col-md-6 mb-3 mt-3">
                                            <label for="position" class="form-label">Position in the Company</label>
                                            <input
                                                 name='position' 
                                                 type="text"
                                                 value={this.state.input.position}
                                                 onChange={this.handleChange} 
                                                 class="form-control"  
                                                 maxlength="20"  
                                                 placeholder="Position Name"
                                                 id='position'/>
                                            </div> 

                                       
                                        <div class="col-md-6 mb-3 mt-3"> 
                                        <div class="input-group "><Image   src={require('./../../assets/images/face.png')}alt="Features tile icon 01"  width={50} height={50}/>
                                            <input
                                                 name='FacebookLink' 
                                                 value={this.state.input.FacebookLink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 class="form-control"  
                                                 maxlength="150"  
                                                 placeholder="Facebook Link"
                                                 id='FacebookLink'/>
                                                 </div>
                                            </div>
                                             
                                        
                                            <div class="col-md-6 mb-3 mt-3">
                                            <div class="input-group ">
                                              <Image   src={require('./../../assets/images/insta.jpg')}alt="Features tile icon 01"  width={50} height={50}/> 
                                            <input
                                                 name='InstagramLink' 
                                                 value={this.state.input.InstagramLink}
                                                 onChange={this.handleChange}
                                                 type="text" 
                                                 class="form-control"  
                                                 maxlength="150"  
                                                 placeholder="Instagram Link"
                                                 id='InstagramLink'/>
                                                 </div>
