import React, {Component} from "react"; 
import Button from '../elements/Button'; 
import Image from "../elements/Image"; 
import '../Css/styles.css';
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import ButtonGroup from "../elements/ButtonGroup"; 
import {NavLink,Link} from "react-router-dom"; 
import DataService from '../../service/DataService'; 
import UploadService from "../../service/file-upload.service";
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09





class Create_Pitch extends React.Component {
    constructor() {
    super();
    this.state = {
      createpitchAllDets: [],
      currentcreatepitch: null,
      currentcreatepitchIndex: -1,
      input: {},
      errors: {},
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      CRT_PITCH_HEADER:"",
      CRT_PITCH_BODY:"",
      CRT_PITCH_IMAGE:"",
      CRT_PITCH_VIDEO:"", 
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:"", 
    };
     
    this.saveCreatePitch = this.saveCreatePitch.bind(this);  
    this.retrieveAllCreatePitch = this.retrieveAllCreatePitch.bind(this);
    this.setActiveCreatePitch = this.setActiveCreatePitch.bind(this);
  }
    
  
  
  componentDidMount() {
    this.retrieveAllCreatePitch(); 
  }


  retrieveAllCreatePitch() {
    var querySet = "/createpitch?EMAIL=" + sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
      .then(response => {
        this.setState({
          createpitchAllDets: response.data, 
        });

         $.each(this.state.createpitchAllDets, function (index, value) {
  

        $("#CreatePitchform #createpitchid").attr("value", value.ID).val(value.ID);
        $("#CreatePitchform #createpitchheader").attr("value", value.CRT_PITCH_HEADER).val(value.CRT_PITCH_HEADER);
        $("#CreatePitchform").find("label[for=createpitchbody]").closest("div").find("div").find("span[data-offset-key]").text(value.CRT_PITCH_BODY)
    
         
         //uploadAreaRaise
        //  if(value.CRT_PITCH_IMAGE.length > 0){
 
        //    $("#Raiseform").find(".uploadAreaRaise").html("<p>Download file: <a class='text-primary' target='_blank' href="+window.mt_backend_url+value.RAISE_UPLOAD_DOC+">"+extractFilename(value.RAISE_UPLOAD_DOC)+"</a></p>")
        //  }else{
        //   $("#Raiseform").find(".uploadAreaRaise").html(' <div class="form-group files"> <input type="file"    className="form-control"  multiple  name="rsuploadfile" id="rsuploadfile" /></div>');
        //  }
        


        // $("#Raiseform #rsuploadfile").attr("value",response.data.TEAM_BIO).val(response.data.TEAM_BIO);
        // $($(".imagePreview")[index]).css("background-image","url("+window.mt_backend_url+response.data.PROFILE_PIC+")");
        // $($("#TeamInfoform #tmuploadprofile")[index]).closest("label").css("visibility","hidden");
        }); 

      })
      .catch(e => {
        console.log(e);
      });
  }
  validateCreatePitch(){
    console.log("inside Raise validations")
    let input = this.state.input;
    let errors = {};
    let isValid = true;

    if (window.isEmpty($("#CreatePitchform #createpitchheader").val())) {
      isValid = false;
      errors["createpitchheader"] = "Please enter header.";
      $("#createpitchheader").focus();
    }
 
    if (window.isEmpty($("#CreatePitchform #createpitchbody").val())) {
      isValid = false;
      errors["createpitchbody"] = "Please enter your Company email Address.";
      $("#createpitchbody").focus();
    }

 
    if (!$("#createpitchimage").get(0).files.length  > 0) {
      isValid = false;
      errors["uploadpitchDoc"] = "Please Upload Pitch";
      $("#uploadpitchDoc").focus();
    }
  
 

    this.setState({
      errors: errors
    });

    return isValid;
  }
     
 render() {
    
 

return (

  <body className='' >
  <div className='container'>  
      <div className='row' style={{height:'auto',marginTop:100}}>
          <div className='row'>
            <div className="hero-content">
               <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                  <div className="container-fluid"  >
                        <ul className="navbar-nav" style={{fontSize:13}}> 
                            <li className="nav-item">
                                <NavLink to="/Startup_Dashboard" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>&nbsp;Back to Dashboard</NavLink>
                              </li>
                        
                          </ul>
                    </div>
                  </nav>

                 <div className='container bg-white p-5'>

                  
                 <div className='row'> 


                <div className='bgactive col-md-1'> <a  href='/Create_Pitch'><Image   src={require('./../../assets/images/p1.png')}alt="Features tile icon 01"  width={50} height={50} borderRadius={50} /><p style={{fontSize:13}}>Problem</p></a> </div>
                   <div className='col-md-1'> <a href='/Solution'><Image   src={require('./../../assets/images/p002.png')}alt="Features tile icon 01"  width={50} height={50} /><p style={{fontSize:13}}>Solution</p></a> </div>
                   <div className='col-md-1'> <a href='/Product'><Image   src={require('./../../assets/images/p003.png')}alt="Features tile icon 01"  width={50} height={50} /><p style={{fontSize:13}}>Product</p></a> </div>
                   <div className='col-md-1'> <a href='/Transaction'><Image   src={require('./../../assets/images/p4.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Transaction</p></a> </div> 
                   <div className='col-md-1'> <a href='/Business_Model'><Image   src={require('./../../assets/images/p5.webp')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Business<br/>Model</p></a> </div>
                   <div className='col-md-1'> <a href='/Competition'><Image   src={require('./../../assets/images/p6.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Competition</p></a> </div>
                   <div className='col-md-1'> <a href='/Customer'><Image   src={require('./../../assets/images/p7.jpg')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Customers</p></a> </div>
                   <div className='col-md-1'> <a href='/Usage'><Image   src={require('./../../assets/images/p8.jpg')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Usage of<br/>Funds</p></a> </div>
                   <div className='col-md-1'> <a href='/Vision'><Image   src={require('./../../assets/images/900.png')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Vision</p></a> </div>
                   <div className='col-md-1'> <a href='/Potential_Returns'><Image   src={require('./../../assets/images/p10.webp')}alt="Features tile icon 01"  width={50} height={50}  /><p style={{fontSize:13}}>Potential<br/>Reutrns</p></a> </div>
                  </div> 
                  
                    <div className='row' style={{height:'auto'}}>
                    <div className='col-md-12'>
                                        <h3>Create Pitch</h3>


                                        <form name="CreatePitchform" id="CreatePitchform" method="POST" enctype="multipart/form-data"  >  

<div className="col-md-12"  > 
  
    <div className="col-md-8 mb-3 ">
    <label for="tmname" className="form-label">Name</label>
    <input name="teaminfoid" id="teaminfoid" type="hidden"  />
     
    <input
                              name="header" 
                              type="text" 
                              value={this.state.input.header} 
                              className="form-control" 
                              placeholder="Enter Your header/title here"
                              length="50" 
                              id="header"/>
                         
          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmname}</div>
    </div> 

    
    <div className="col-md-8 mb-3 mt-3" >
    <label for="tmposition" className="form-label">Position in the Company</label>
    <div style={{ height:'300px',backgroundColor:"white"}} className="border">
                  
    <Editor  
                      toolbarClassName="toolbarClassName"
                      wrapperClassName="wrapperClassName"
                      editorClassName="editorClassName"
                      name="crasummarnoteetae"
                      value={this.state.input.summarnote}
                       
                      id="summarnote" 
                    />
                    </div>
          <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmposition}</div>
    </div> 
 

  <div className="col-md-8 mb-3 mt-3">
    <div className="row mb-3">
    <div className="col-md-8">
    <label for="tmuploadprofile" className="col-sm-2 form-label">Images</label>
    </div>

    <div className="col-md-4">     
          <label className="col-md-12 btn btn-outline-secondary btn-sm">
                Upload a picture<br/>
                <input type="file" id="tmuploadprofile" name="tmuploadprofile" 
                accept="image/*" data-attr="1" className=" uploadFile img"  multiple 
                style={{height:"0px",width:"0px",overflow : "hidden"}}/>
                  </label>
                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmuploadprofile}</div>
            </div>   

    </div>
    <div className="row mb-3 card">
      <h4>List of Images</h4>

    </div>
  </div>



  <div className="col-md-8 mb-3 mt-3">
    <div className="row mb-3">
    <div className="col-md-8">
    <label for="tmuploadprofile" className="col-sm-2 form-label">Video(s)</label>
    </div>

    <div className="col-md-4">     
          <label className="col-md-12 btn btn-outline-secondary btn-sm">
                Upload a video<br/>
                <input type="file" id="tmuploadprofile" name="tmuploadprofile" 
                  data-attr="1" className=" uploadFile img"  multiple 
                style={{height:"0px",width:"0px",overflow : "hidden"}}/>
                  </label>
                  <div className="text-danger errors" style={{fontSize:15}}>{this.state.errors.tmuploadprofile}</div>
            </div>   

    </div>


    <div className="row mb-3 card">
    <h4>List of Videos</h4>
    </div>

  </div>
     
 </div>
 <div className="col-md-12 mt-5">
 <div className="row">

<button type="button" className="btn btn-success btn-sm col-md-4"  onClick={this.saveTeamInfo}>Next</button>



</div>
 </div>


</form>




                                        </div>

                    </div></div>
    
                    </div>
                    
                    </div>
                    
                    
                    </div></div> 
 

    </body>          
              
              

              


              
               
               

) ;       
 }
};



export default Create_Pitch;
