import React, { Component } from "react";
import "../Css/styles.css";
import { NavLink, Link } from "react-router-dom";
import DataService from "../../service/DataService"; 

import Moment from "moment";
import $ from "jquery";

const current = new Date();
const date = `${current.getFullYear()}-${
  current.getMonth() + 1
}-${current.getDate()}`; //mysql insert date format
const formatDate = Moment("12/09/2002").format("YYYY-MM-DD"); //2002-12-09

let linkRaise = "";

const extractFilename = (path) => {
  const pathArray = path.split("/");
  const lastIndex = pathArray.length - 1;
  return pathArray[lastIndex];
};

class Investors_Startups  extends React.Component {
  constructor() {
    super();
    this.retriveInvestorStartups = this.retriveInvestorStartups.bind(this);
  }

  componentDidMount() {
    this.retriveInvestorStartups();
  }
  retriveInvestorStartups() {
    var inv_termsDets = [];
  let status = "";
  var termsquerySet = "/inv_terms?EMAIL=" + sessionStorage.getItem("sessEmail");
  DataService.findByTitle(termsquerySet)
    .then((response) => {
      inv_termsDets = response.data;
      console.log(inv_termsDets.length);
      $.each(inv_termsDets, function (index, value) {
        status = value.STATUS;
        // console.log("LENGTH OF COMPANY INFO"+value.STATUS);

        if (status == "Dormant") {
          $(".investorpage .invstatusbtn").html(
            '<a href="/Inv_Signup"><button type="button" class="btn btn-success btn-sm" >SIGNUP NOW</button></a>'
          );
        } else if (!window.isEmpty(sessionStorage.getItem("sessEmail"))) {
          $(".investorpage .invstatusbtn").html(
            '<a href="/Investor_Signup"><button type="button" class="btn btn-success btn-sm" >Investor Dashboard</button></a>'
          );
        }
      });
    })
    .catch((e) => {
      console.log(e);
    });


  }

  render() {
    return (
      <>
        <div className="container-fluid ">
        <div className="container">
        <div className="row d-flex justify-content-center investorpage">
          <div
            className="card border-0 mt-5"
            style={{ background: "#e4b21f" }}
          >
            <div className="card-body  ">
              <div className="row">
                <div className="col-md-8">
                  <h1 class="card-title">
                   Invest, Diversify, Enable
                    <span style={{ color: "#e4b21f" }}>Startups</span>
                  </h1>
                  <p class="card-text">
                    <span>
                      Discover highly curated, vetted investment opportunities{" "}
                      <br />
                      today and{" "}
                      <span style={{ color: "#e4b21f" }}>
                        enable the future
                      </span>{" "}
                    </span>
                  </p>
                  <div className="row invstatusbtn col-md-6 mt-2">
                    <a href="/Investor_Signup">
                      <button type="button" class="btn btn-dark btn-sm">
                        SIGNUP NOW
                      </button>
                    </a>
                  </div>{" "}
                  <br /> <br />
                  <p className="para">
                    Investments Are Risky And May Result in Total <br />
                    Loss Of Capital.&nbsp;
                    <a href="#" className="alert-link">
                      <u>Learn More</u>
                    </a>
                  </p>
                </div>
                <div className="col-md-4 investWid">
                  <div class="card  profile-card-4">
                    {/* <img src="..." class="card-img-top" alt="..."/> */}
                    <div class="card-body text-center">
                      <h6 class="card-title">If you had invested *</h6>
                      <p class="card-text" id="demo">
                        <i className="fa fa-rupee"></i>&nbsp;5,000
                      </p>

                      {/* <div class="progress">
  <div class="progress-bar bg-info" role="progressbar" style={{width: "50%"}} aria-valuenow="50" aria-valuemin="0" aria-valuemax="100"></div>
</div> */}
                      <div class="slidecontainer">
                        <input
                          id="investorRange"
                          type="range"
                          min="1"
                          max="100"
                          value="50"
                        />
                      </div>

                      <p className="para text-muted">
                        You could have theoretically made
                      </p>
                      <hr />
                    </div>
                    <div style={{ height: "219px", paddingLeft: "43px" }}>
                      <table
                        className="table table-borderless table-hover table-lg"
                        border="0"
                      >
                        <marquee direction="up" loop="true" scrollamount="10">
                          <tr className="border-0 para ">
                            <td>
                              <img
                                src={require("./../../assets/images/ola.png")}
                                alt="Features tile icon 01"
                                width={70}
                                height={70}
                              />
                            </td>
                            <td>Ola</td>
                            <td>
                              <i className="fa fa-rupee"></i>&nbsp;12.5M
                            </td>
                          </tr>
                          <tr className="border-0 para">
                            <td>
                              <img
                                src={require("./../../assets/images/byjus.png")}
                                alt="Features tile icon 01"
                                width={70}
                                height={70}
                              />
                            </td>
                            <td>Byjus</td>
                            <td>
                              <i className="fa fa-rupee"></i>&nbsp;3.3M
                            </td>
                          </tr>
                          <tr className="border-0 para">
                            <td>
                              <img
                                src={require("./../../assets/images/unacedamy.png")}
                                alt="Features tile icon 01"
                                width={70}
                                height={70}
                              />
                            </td>
                            <td>Unacademy</td>
                            <td>
                              <i className="fa fa-rupee"></i>&nbsp;1.7M
                            </td>
                          </tr>
                          <tr className="border-0 para">
                            <td>
                              <img
                                src={require("./../../assets/images/razor.png")}
                                alt="Features tile icon 01"
                                width={70}
                                height={70}
                              />
                            </td>
                            <td>RazorPay</td>
                            <td>
                              <i className="fa fa-rupee"></i>&nbsp;625K
                            </td>
                          </tr>
                        </marquee>
                        <tr className="border-0 para text-center">
                          <td colspan="3" className="para text-muted">
                            *In the initial rounds
                          </td>
                        </tr>
                      </table>
                    </div>
                    {/* <div class="card-body">
    <a href="#" class="card-link">Card link</a>
    <a href="#" class="card-link">Another link</a>
  </div> */}
                  </div>
                </div>
              </div>

              <br />
            </div>
          </div>
        </div>
      </div>

        </div> 
      </>
    );
  }
}

export default Founders;
