  newUser() {
    this.setState({
      id: null,
      FIRSTNAME : "",
      LASTNAME : "",
      EMAIL : "",
      SCHOOL : "",
      PASSWORD : "",
      CONPASSWORD : "",
      AGREECHK : null,
      COMMENTS: null,
      CREATED_DATE: null,
      CREATED_USER: null,
      DESCRIPTION: null,
      MODIFIED_DATE: null,
      MODIFIED_USER: null,
      MODULE:null,
      ROLE: null,
      STATUS: null,
    });
