import React, {Component} from "react";
import { NavLink } from "react-router-dom";
import '../Css/styles.css';
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09



class Campaign_Banner extends React.Component {
  constructor() {
    super();
    this.state = {
      bannerAllDets: [],
      currentbanner: null,
      currentbannerIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      BANNER_SNO:"",
      BANNER_IMAGE:"",
      BANNER_VIDEO:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.saveBanner = this.saveBanner.bind(this);  
    this.retrieveAllBanner = this.retrieveAllBanner.bind(this);
    this.setActiveBanner = this.setActiveBanner.bind(this);
    
  } 
     
  componentDidMount() {
    this.retrieveAllBanner();
  }
  
  retrieveAllBanner() {
    var querySet = "/banner?EMAIL="+sessionStorage.getItem("sessEmail");
    console.log(querySet);
    DataService.findByTitle(querySet)
     .then(response => {
       this.setState({
        bannerinfoAllDets: response.data, 
       });
       
        this.state.bannerinfoAllDets.map((banner, index) => (
            $("#bannerid").attr("value",banner.ID),
            $("#bannersno").attr("value",banner.BANNER_SNO)
            // $("#tmprofileinfo").attr("value",cinfo.PROFILE_PIC)
            
          ));
       
     })
     .catch(e => {
       console.log(e);
     });
  }
  
  
  setActiveBanner(bannerAllDets, index) {
  this.setState({
    currentbanner: bannerAllDets,
    currentbannerIndex: index
  });
  } 
     
  
  saveBanner(event) {
  event.preventDefault();  
  if(this.validateBanner()){
    console.log("inside Campaign Banner save")
  if(!window.isEmpty(sessionStorage.getItem("sessEmail"))){ 
    
    // var teaminfodata={}
     
    $("#BannerForm #bruploadimage").each(function (bannerset) {
      // console.log(teamset)
      //  console.log($($("#TeamInfoform #tmname")[teamset]).val()) 
       
      var bannerdata = {
        ID:Number($($("#BannerForm #bannerid")[bannerset]).val()),
        MTUSER_ID:$("#mtuser_id").val(),
        EMAIL:$("#mtuser_email").val(),
        MODULE:$("#mtuser_module").val(),
        BANNER_SNO:$($("#BannerForm #bannersno")[bannerset]).val(),
        BANNER_IMAGE:null,
        BANNER_VIDEO:null,
        STATUS:"Active",
        COMMENTS:"TEST",
        DESCRIPTION:"TEST",
        CREATED_USER:$("#mtuser_fname").val(),
        CREATED_DATE:Moment(date).format("YYYY-MM-DD"),
        MODIFIED_USER:$("#mtuser_fname").val(),
        MODIFIED_DATE:Moment(date).format("YYYY-MM-DD")
       }
  
  if(window.isEmpty($("#bannerid").val())){ 
  
  DataService.create("/banner",bannerdata)
    .then(response => {  
        window.showLoader();  
        window.showAlert("Campaign Banner is submittted","/Campaign");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/Campaign_Banner");
    });
  
  }else{ 
  
    DataService.update("/banner"+$("#bannerid").val(),bannerdata)
    .then(response => {  
        window.showLoader();  
      window.showAlert("Campaign Banner is submittted","/Campaign");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/Campaign_Banner");
    });
  
  }
  
  
      });
    
   
  
  }else{
    window.showAlert("Please Login","/Login");
  }
  }
  
  }  
  validateBanner(){
  console.log("inside Campaign Banner validations")
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      $("#BannerForm #bruploadimage").each(function (index) {
       
        if (window.isEmpty($($("#BannerForm #bruploadimage")[index]).val())) {
          isValid = false;
          errors["bruploadimage"] = "Please Upload a Image.";
        }
     
        if (window.isEmpty($($("#BannerForm #bruploadvideo")[index]).val())) {
          isValid = false;
          errors["bruploadvideo"] = "Please Upload a Video ";
        }
  
      });
      
     
       
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
  render() {
  const {  bannerAllDets, currentbanner, currentbannerIndex } = this.state;
return (

    <body>
    
             
        <div className="row" >
  
               <div className="row">
               <div className="col-md-1"></div>
               <div className="col-md-11">
               
               <div className="m-5 p-4">
               <div className="row">
               <div className="row" >
               <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item">
                                          <NavLink to="/Campaign" className="nav-link arrow-right" style={{color:"#B1B0AD"}}>Back to Campaign</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
                            </nav>
              </div>              
              </div>
            
              

                <br/>
                <div className='row'>
                    <div className='col-md-12'><strong style={{fontSize:25}}>Banner Video</strong></div>
                    &nbsp;
                    &nbsp;
                    <p style={{fontSize:13}}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pulvinar commodo eget turpis est dolor Pulvinar cras elementum eget. purus interdum semper accumsan metus est aenean turpis. Neque, amet amet turpis quam nisi ut Venenatis. Eu in suscipit quis non. Amet, non elit morbi eu. Nisi, mauris quam eget aliquet. Malesuada suspendisse at aliquam convallis inerdum.</p>

                </div>
                <form name="BannerForm" id="BannerForm" method="POST">
                <div className="bannermem" id="bannerDivisionIter">


                
                <div className="row">
                    
