import React from 'react';
import '../Css/styles.css';
import {NavLink,Link} from "react-router-dom";
import Image from '../elements/Image';
import Button from '../elements/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonGroup from '../elements/ButtonGroup';
import classNames from 'classnames'; 
import DataService from '../../service/DataService'; 
import Moment from 'moment';
  
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
 
const formatDate = Moment().format('YYYY-DD-MM');


class Company_Info extends React.Component {
    constructor() {
    super();
    
    this.state = {
      companyAllDets: [],
      currentCinfo: null,
     currentCinfoIndex: -1,
      input: {},
      errors: {},
      ID: null,
      MTUSER_ID:null,
      EMAIL:"",
      MODULE:"",
      COUNTRY:"",
      STATE:"",
      CITY:"",
      PINCODE:"",
      ADDRESS:"",
      COMPANY_WEBSITE:"",
      FB_LINK:"",
      INSTA_LINK:"",
      LINKEDIN_LINK:"",
      LEGAL_NAME:"",
      CIN_NUMBER:"",
      DATE_OF_INCORPORATATION:"",
      ABOUT_COMPANY:"",
      AMOUNT_INVESTED:"",
      NO_OF_EMPLOYEES:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.saveCompanyInfo = this.saveCompanyInfo.bind(this); 
    this.handleChange = this.handleChange.bind(this);
    this.retrieveAllCompanyInfo = this.retrieveAllCompanyInfo.bind(this);
    this.setActiveCinfo = this.setActiveCinfo.bind(this);

  }
     
  componentDidMount() {
    this.retrieveAllCompanyInfo();
  }
  
 setActiveCinfo(companyAllDets, index) {
  this.setState({
    currentCinfo: companyAllDets,
    currentCinfoIndex: index
  });
}
  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value; 

    this.setState({
      // ID: null,
      MTUSER_ID:document.getElementById("mtuser_id").value,
      EMAIL:document.getElementById("mtuser_email").value,
      MODULE:document.getElementById("mtuser_module").value,
      COUNTRY:this.state.input.cicountry,
      STATE:this.state.input.cistate,
      CITY:this.state.input.cicity,
      PINCODE:this.state.input.cipincode,
      ADDRESS:this.state.input.ciaddress,
      COMPANY_WEBSITE:this.state.input.ciCompanyWebsite,
      FB_LINK:this.state.input.ciFacebookLink,
      INSTA_LINK:this.state.input.ciInstagramLink,
      LINKEDIN_LINK:this.state.input.ciLinkedInLink,
      LEGAL_NAME:this.state.input.ciLegalName,
      CIN_NUMBER:this.state.input.ciCinNumber,
      DATE_OF_INCORPORATATION:date,
      ABOUT_COMPANY:this.state.input.ciSector,
      AMOUNT_INVESTED:this.state.input.ciAmountInvested,
      NO_OF_EMPLOYEES:this.state.input.ciEmployees,
      STATUS:"Active",
      COMMENTS:"TEST",
      DESCRIPTION:"TEST",
      CREATED_USER:document.getElementById("mtuser_fname").value,
      CREATED_DATE:date,
      MODIFIED_USER:document.getElementById("mtuser_fname").value,
      MODIFIED_DATE:date
    });
  }
  saveCompanyInfo(event) {
    event.preventDefault();  
    // if(this.validateCompanyInfo()){
    if(!window.isEmpty(document.getElementById("mtuser_id").value)){   
    var cidata = {
      MTUSER_ID:document.getElementById("mtuser_id").value,
      EMAIL:document.getElementById("mtuser_email").value,
      MODULE:document.getElementById("mtuser_module").value,
      COUNTRY:this.state.input.cicountry,
      STATE:this.state.input.cistate,
      CITY:this.state.input.cicity,
      PINCODE:this.state.input.cipincode,
      ADDRESS:this.state.input.ciaddress,
      COMPANY_WEBSITE:this.state.input.ciCompanyWebsite,
      FB_LINK:this.state.input.ciFacebookLink,
      INSTA_LINK:this.state.input.ciInstagramLink,
      LINKEDIN_LINK:this.state.input.ciLinkedInLink,
      LEGAL_NAME:this.state.input.ciLegalName,
      CIN_NUMBER:this.state.input.ciCinNumber,
      DATE_OF_INCORPORATATION:date,
      ABOUT_COMPANY:this.state.input.ciSector,
      AMOUNT_INVESTED:this.state.input.ciAmountInvested,
      NO_OF_EMPLOYEES:this.state.input.ciEmployees,
      STATUS:"Active",
      COMMENTS:"TEST",
      DESCRIPTION:"TEST",
      CREATED_USER:document.getElementById("mtuser_fname").value,
      CREATED_DATE:date,
      MODIFIED_USER:document.getElementById("mtuser_fname").value,
      MODIFIED_DATE:date
    };
  
   console.log(cidata);
    DataService.create("/company_info",cidata)
      .then(response => {  
          window.showLoader(); 
        this.setState({
          ID: response.data.ID,
          MTUSER_ID  :response.data.MTUSER_ID ,
          EMAIL : response.data.EMAIL,
          MODULE:response.data.MODULE,
          COUNTRY:response.data.COUNTRY,
          STATE:response.data.STATE,
          CITY:response.data.CITY,
          PINCODE:response.data.PINCODE,
          ADDRESS:response.data.ADDRESS,
          COMPANY_WEBSITE:response.data.COMPANY_WEBSITE,
          FB_LINK : response.data.FB_LINK,
          INSTA_LINK : response.data.INSTA_LINK,
          LINKEDIN_LINK : response.data.LINKEDIN_LINK,
          LEGAL_NAME:response.data.LEGAL_NAME,
          CIN_NUMBER:response.data.CIN_NUMBER,
          DATE_OF_INCORPORATATION:response.data.DATE_OF_INCORPORATATION,
          INCORPORATION_TYPE:response.data.INCORPORATION_TYPE,
          ABOUT_COMPANY:response.data.ABOUT_COMPANY,
          AMOUNT_INVESTED:response.data.AMOUNT_INVESTED,
          NO_OF_EMPLOYEES:response.data.NO_OF_EMPLOYEES,
          STATUS: response.data.STATUS,
          COMMENTS:response.data.COMMENTS,
          DESCRIPTION: response.data.DESCRIPTION,
          CREATED_USER: response.data.CREATED_USER,
          CREATED_DATE: response.data.CREATED_DATE,
          MODIFIED_USER: response.data.MODIFIED_USER,
          MODIFIED_DATE: response.data.MODIFIED_DATE,
          
        });  
        window.showAlert("Company Information is submittted","/Startup_Dashboard");
      })
      .catch(e => {
        window.showLoader();  
        window.showAlert("OOps!!! Something went wrong","/Company_Info");
      });
    }else{
      window.showAlert("Please Login","/Login");
    }
  // }

  }  
   
  validateCompanyInfo(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      if (!input["country"]) {
        isValid = false;
        errors["country"] = "Please enter your country.";
      }
  
      if (typeof input["country"] !== "undefined") {
        const re = /^\S*$/;
        if(input["country"].length < 2 || !re.test(input["country"])){
            isValid = false;
            errors["country"] = "Please enter valid country.";
        }
      }
  
      if (!input["state"]) {
        isValid = false;
        errors["state"] = "Please enter your state.";
      }
  
      if (typeof input["state"] !== "undefined") {
        const re = /^\S*$/;
        if(input["state"].length < 3 || !re.test(input["state"])){
            isValid = false;
            errors["state"] = "Please enter valid state.";
        }
      }
      

      if (!input["city"]) {
        isValid = false;
        errors["city"] = "Please enter your city.";
      }
  
      if (typeof input["city"] !== "undefined") {
        const re = /^\S*$/;
        if(input["city"].length < 3 || !re.test(input["city"])){
            isValid = false;
            errors["city"] = "Please enter valid city.";
        }
      }

      if (!input["pincode"]) {
        isValid = false;
        errors["pincode"] = "Please enter your pincode.";
      }
  
      if (typeof input["pincode"] !== "undefined") {
        const re = /^\S*$/;
        if(input["pincode"].length < 6 || !re.test(input["pincode"])){
            isValid = false;
            errors["pincode"] = "Please enter valid pincode.";
        }
      }

      if (!input["address"]) {
        isValid = false;
        errors["address"] = "Please enter your address.";
      }
  
      if (typeof input["address"] !== "undefined") {
        const re = /^\S*$/;
        if(input["address"].length < 6 || !re.test(input["address"])){
            isValid = false;
            errors["address"] = "Please enter valid address.";
        }
      }

      if (!input["CompanyWebsite"]) {
        isValid = false;
        errors["CompanyWebsite"] = "Please enter your Company website.";
      }
  
      if (typeof input["CompanyWebsite"] !== "undefined") {
        const re = /^\S*$/;
        if(input["CompanyWebsite"].length < 6 || !re.test(input["CompanyWebsite"])){
            isValid = false;
            errors["CompanyWebsite"] = "Please enter your Company Website.";
        }
      }

      if (!input["FacebookLink"]) {
        isValid = false;
        errors["FacebookLink"] = "Please enter your Facebook Link.";
      }
  
      if (typeof input["FacebookLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["FacebookLink"].length < 6 || !re.test(input["FacebookLink"])){
            isValid = false;
            errors["FacebookLink"] = "Please enter your Facebook Link.";
        }
      }

      if (!input["InstagramLink"]) {
        isValid = false;
        errors["InstagramLink"] = "Please enter your Instagram Link.";
      }
  
      if (typeof input["InstagramLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["InstagramLink"].length < 6 || !re.test(input["InstagramLink"])){
            isValid = false;
            errors["InstagramLink"] = "Please enter your Instagram Link.";
        }
      }

      if (!input["LinkedInLink"]) {
        isValid = false;
        errors["LinkedInLink"] = "Please enter your Linked In Link.";
      }
  
      if (typeof input["LinkedInLink"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LinkedInLink"].length < 6 || !re.test(input["LinkedInLink"])){
            isValid = false;
            errors["LinkedInLink"] = "Please enter your Linked In Link.";
        }
      }

      if (!input["LegalName"]) {
        isValid = false;
        errors["LegalName"] = "Please enter your Legal Name.";
      }
  
      if (typeof input["LegalName"] !== "undefined") {
        const re = /^\S*$/;
        if(input["LegalName"].length < 6 || !re.test(input["LegalName"])){
            isValid = false;
            errors["LegalName"] = "Please enter your Legal Name.";
        }
      }

      if (!input["CinNumber"]) {
        isValid = false;
        errors["CinNumber"] = "Please enter your CIN Number.";
      }
  
      if (typeof input["CinNumber"] !== "undefined") {
        const re = /^\S*$/;
        if(input["CinNumber"].length < 6 || !re.test(input["CinNumber"])){
            isValid = false;
            errors["CinNumber"] = "Please enter your CIN Number.";
        }
      }

      if (!input["Date"]) {
        isValid = false;
        errors["Date"] = "Please enter Date of Incorporation.";
      }

      if (!input["Incorporation"]) {
        isValid = false;
        errors["Incorporation"] = "Please enter your Incorporation Type.";
      }
  
      if (typeof input["Incorporation"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Incorporation"].length < 6 || !re.test(input["Incorporation"])){
            isValid = false;
            errors["Incorporation"] = "Please enter your Incorporation Type.";
        }
      }

      if (!input["Sector"]) {
        isValid = false;
        errors["Sector"] = "Please enter Sector.";
      }
  
      if (typeof input["Sector"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Sector"].length < 3 || !re.test(input["Sector"])){
            isValid = false;
            errors["Sector"] = "Please enter Sector.";
        }
      }


      if (!input["AmountInvested"]) {
        isValid = false;
        errors["AmountInvested"] = "Enter Amount Invested in Business till date.";
      }
  
      if (typeof input["AmountInvested"] !== "undefined") {
        const re = /^\S*$/;
        if(input["AmountInvested"].length < 2 || !re.test(input["AmountInvested"])){
            isValid = false;
            errors["AmountInvested"] = "Enter Amount Invested in Business till date.";
        }
      }


      if (!input["Employees"]) {
        isValid = false;
        errors["Employees"] = "Enter No. of Employees.";
      }
  
      if (typeof input["Employees"] !== "undefined") {
        const re = /^\S*$/;
        if(input["Employees"].length < 1 || !re.test(input["Employees"])){
            isValid = false;
            errors["Employees"] = "Enter No. of Employees.";
        }
      }

  
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
  render() {
    const {  companyAllDets, currentCinfo, currentCinfoIndex } = this.state;
    return (
        <>
        <body className='bg-white' >
            <div className='container'>
                       
                <div className='row' style={{height:'auto',marginTop:100}}>
                    <div className='row'>
                      <div className="hero-content">
                         <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
                            <div className="container-fluid"  >
                                  <ul className="navbar-nav" style={{fontSize:13}}> 
                                      <li className="nav-item ">
                                          <NavLink to="/Startup_Dashboard" className="nav-link arrow-right"
                                           style={{color:"#B1B0AD"}}> Back to Dashboard</NavLink>
                                        </li>
                                  
                                    </ul>
                              </div>
                            </nav>

                           <div className='container'>
                           <form name="CompanyInfoform" id="CompanyInfoform" method="POST" className="row m-5 g-3"  > 
          
                             
                               <div className='row' style={{height:'auto'}}>
                                    <div className='col-md-12'>
                                        <h3>Company Info</h3>
                                        <div className="row">
                            <ul class="nav nav-tabs">
  <li class="nav-item">
    <a class="nav-link active font16" data-bs-toggle="tab" href="#companyinfo"> 
    <b>Application</b></a>
  </li>
  
</ul>
 
<div class="tab-content">
  <div class="tab-pane container active" id="companyinfo">
  {/* <p className='para'>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Tincidunt libero vel elementum at cum tupis eget. Viverra ultrices lacus, lectus volutpat sociis vitae mauris<br/>porta faugiat. Nec, vitae facilisi elementum eu est vel quis platea. Diam pharetra nec malesuada mi purus erat.</p> */}
  <div class="col-md-6 mb-3 mt-3">
    <label for="cicountry" class="form-label">Country</label>
    <input  name="cicountry" 
          value={this.state.input.cicountry}
          onChange={this.handleChange}
          type="text" 
          class="form-control"  
          maxlength="20"  
          placeholder="Enter Your Country here"
          id="cicountry"
           />
  </div>
  <div class="col-md-6 mb-3">
    <label for="cistate" class="form-label">State</label>
    <input 
            name="cistate" 
            value={this.state.input.cistate}
            onChange={this.handleChange}                                            
            type="text" 
            class="form-control"  
            maxlength="20"  
            placeholder="Enter Your State here"
            id="cistate"
            />
  </div>   




  <div className="col-md-6 mb-3"  >   
  <label for="cicity" class="form-label">City</label>
                                            
            <input 
            name="cicity"
            value={this.state.input.cicity}
            onChange={this.handleChange}
            type="text" 
            class="form-control"  
            maxlength="20"  
            placeholder="Enter Your City here"
            id='cicity'
            />
      </div>
      <div class="col-md-6 mb-3"  >
              <label for="cipincode" class="form-label">Pin Code</label>
                    <input 
                    name='cipincode'
                    value={this.state.input.cipincode}
                    onChange={this.handleChange}
                    type="text" 
                    class="form-control"  
                    maxlength="6"  
                    placeholder="Enter Your Pin Code here"
                    id='cipincode'
                    />
              </div>

    <div class="col-md-6 mb-3" >
<label for="ciaddress" class="form-label">Address</label>
<input 
    name='ciaddress'
    value={this.state.input.ciaddress}
    onChange={this.handleChange}
    type="text" 
    class="form-control"  
    maxlength="150"  
    placeholder="Enter Your Company Address here"
    id='ciaddress'/>
</div>                           

    </div> 
</div>


                            </div>


 {/* 2nd Online presence */}
 <div className='col-md-12'>
                                         
                                        <div className="row">
                            <ul className="nav nav-tabs">
  <li className="nav-item">
    <a className="nav-link active font16" data-bs-toggle="tab" href="#onlinepresence"> 
    <b>Online presence</b></a>
  </li>
  
</ul>
 
  <div className="tab-content">
  <div className="tab-pane container active" id="onlinepresence">

  <div className="col-md-6 mb-3" >
          <label for="ciCompanyWebsite" className="form-label">Company Website</label>
          <input
              name='ciCompanyWebsite' 
              value={this.state.input.ciCompanyWebsite}
              onChange={this.handleChange}
              type="text" 
              className="form-control"  
              maxlength="150"  
              placeholder="Enter Your Company Website here" 
              id='ciCompanyWebsite'/>
          </div>
          
          <div className="col-md-6 mb-3"  >
          <label   className="form-label">Social Media Handles</label>

          <div className="input-group ">
          <Image  src={require('./../../assets/images/face.png')} alt="Features tile icon 01"  width={50} height={50}/>
          <input
              name='ciFacebookLink' 
              value={this.state.input.ciFacebookLink}
              onChange={this.handleChange}
              type="text" 
              className="form-control"  
              maxlength="150"  
