import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { SectionProps } from '../../utils/SectionProps';
import Button from '../elements/Button';
import ButtonGroup from '../elements/ButtonGroup';
import { NavLink } from 'react-router-dom';
import Image from '../elements/Image';
import DataService from '../../service/DataService';
import $ from "jquery"; 



var slider = document.getElementById("investorRange");
var output = document.getElementById("demo");
output.innerHTML = slider.value;

slider.oninput = function() {
  output.innerHTML = this.value;
}







const propTypes = {
  ...SectionProps.types,
  split: PropTypes.bool
}

const defaultProps = {
  ...SectionProps.defaults,
  split: false
}

const Investors_Startups= ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  split,
  pushLeft,
  ...props
}) => {

  const outerClasses = classNames(
    'cta section center-content-mobile reveal-from-bottom',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'cta-inner section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider',
    split && 'cta-split'
  );  

  const tilesClasses = classNames(
    'tiles-wrap',
    pushLeft && 'push-left'
  );

  var raiseDets=[]; 
  let status="";
  var raisequerySet = "/raise?EMAIL="+sessionStorage.getItem("sessEmail");
  DataService.findByTitle(raisequerySet)
  .then(response => {
    raiseDets = response.data 
    $.each(raiseDets, function (index, value) {
      status=value.STATUS;
      console.log("LENGTH OF COMPANY INFO"+value.STATUS);
      if(status == "Dormant"){
        $(".investorpage .invstatusbtn").html('<a href="/Signup"><button type="button" class="btn btn-success btn-sm" > SIGNUP NOW</button></a>')
      }else{
        $(".investorpage .invstatusbtn").html('<a href="/Invest_Now"><button type="button" class="btn btn-success btn-sm" id="investordashboard"> INVEST NOW  </button></a>');
      }
    });
      
     })
     .catch(e => {
       console.log(e);
     });



  return (
    <section
      {...props}
      className={outerClasses}>
       {/* <div className={tilesClasses} align="center">
          <div className="hero-content">
          <nav className="navbar navbar-expand-sm bg-Secondary navbar-white " >
            <div className="container-fluid">
              <ul className="navbar-nav">
                
              <li className="nav-item">
                  <NavLink to="/Investors" className="nav-link" style={{color:" #2ECC71"}}>Startups</NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/Crypto" className="nav-link" >Crypto</NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/Realestate" className="nav-link" >Real Estate</NavLink>
                </li>
              </ul>
            </div>
          </nav>
          </div>
         </div> */}
      <div className="container bg-white">



        <div className="row mt-2  d-flex justify-content-center investorpage">
    <div className="card border-0"   style={{background:"rgb(249, 250, 249)"}}>
  <div className="card-body  ">
    <div className="row">
<div  className="col-md-8">
  <h1 class="card-title">Invest Now In <span style={{color:"#2ECC71"}}>Startups</span></h1>
    <p class="card-text"><span>Discover highly curated, vetted investment opportunities <br/>today and <span style={{color:"#2ECC71"}}>enable the future</span> </span></p>
    <div className='row invstatusbtn col-md-6 mt-2'>
                <a href="/Signup"><button type="button" class="btn btn-success btn-sm" >SIGNUP NOW</button></a>
                </div> <br/> <br/>
    <p className="para">Investments Are Risky And May Result in Total <br/>Loss Of Capital.&nbsp;
                <a href="#" className="alert-link"><u>Learn More</u></a>
                </p>



</div>
<div  className="col-md-4 investWid">
<div class="card m-2 profile-card-4" >
  {/* <img src="..." class="card-img-top" alt="..."/> */}
  <div class="card-body text-center">
    <h5 class="card-title">If you had invested *</h5>
    <p class="card-text"><i className="fa fa-rupee"></i>&nbsp;5,000</p>
    <p>
    {/* <div class="progress">
  <div class="progress-bar bg-info" role="progressbar" style={{width: "50%"}} aria-valuenow="50" aria-valuemin="0" aria-valuemax="100"></div>
</div> */}
<div class="slidecontainer"> 
  <input id="investorRange" type="range" min="1" max="100" value="50" className="slider"/>
    </div>
    </p>
    <p className="para text-muted">You could have theoretically made</p>
  </div>
  <ul class="list-group list-group-flush">
    <li class="list-group-item">An item</li>
    <li class="list-group-item">A second item</li>
    <li class="list-group-item">A third item</li>
  </ul>
  <div class="card-body">
    <a href="#" class="card-link">Card link</a>
    <a href="#" class="card-link">Another link</a>
  </div>
</div>


 
  </div>
  </div>


                <br/>
                

</div>


 
              
                
               
            



</div>
</div>











        <div
          className={innerClasses} style={{backgroundColor:"#f9faf9"}}>
          <div className="cta-slogan investorpage">
            <h1 className="m-0" >
              Invest Now In <span style={{color:"#2ECC71"}}>Startups</span></h1><br/>
              <span>Discover highly curated, vetted investment opportunities <br/>today and <span style={{color:"#2ECC71"}}>enable the future</span> </span>
             &nbsp;
              <div   style={{paddingTop:30}} >
              <div className='row invstatusbtn col-md-6'>
                <a href="/Signup"><button type="button" class="btn btn-success btn-sm" >SIGNUP NOW</button></a>
                </div>
                
                <br/>
                <p>Investments Are Risky And May Result in Total <br/>Loss Of Capital.<u>Learn More</u></p>
              </div>
          </div>
          <div className="cta-action"> 
          <Image
                      src={require('./../../assets/images/Investors.png')}
                      alt="Features tile icon 01"
                      width={300}
                      height={300}
                     />
          </div>
        </div>
      </div>
    </section>
  );
}

Investors_Startups.propTypes = propTypes;
Investors_Startups.defaultProps = defaultProps;

export default Investors_Startups;
