import React, {Component} from "react";
import classNames from 'classnames';
import Button from '../elements/Button';
import { NavLink } from "react-router-dom";
import '../Css/styles.css';
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import 'bootstrap/dist/css/bootstrap.min.css'; 
import DataService from '../../service/DataService'; 
import Moment from 'moment';
import $ from "jquery";
const current = new Date();
const date = `${current.getFullYear()}-${current.getMonth()+1}-${current.getDate()}`;//mysql insert date format
const formatDate = Moment("12/09/2002").format('YYYY-MM-DD')//2002-12-09



class Campaign_Press extends React.Component {
  constructor() {
    super();
    this.state = {
      pressinfoAllDets: [],
      currentpressinfo: null,
      currentpressinfoIndex: -1,
      input: {},
      errors: {},
      profile : "Upload a Profile",
      ID:null,
      MTUSER_ID:null,
      EMAIL:null,
      MODULE:null,
      PRESS_SNO:"",
      PRESS_HEADER:"",
      PRESS_BODY:"",
      PRESS_IMAGE:"",
      PRESS_VIDEO:"",
      STATUS:"",
      COMMENTS:"",
      DESCRIPTION:"",
      CREATED_USER:"",
      CREATED_DATE:"",
      MODIFIED_USER:"",
      MODIFIED_DATE:""
    };
     
    this.savePressInfo = this.savePressInfo.bind(this);  
    this.retrieveAllPressInfo = this.retrieveAllPressInfo.bind(this);
    this.setActivePressinfo = this.setActivePressinfo.bind(this);
    
  }
     
  componentDidMount() {
    this.retrieveAllPressInfo();
  }
  
