  }else{ 
  
    DataService.update("/pressinfo"+$("#pressinfoid").val(),pressinfodata)
    .then(response => {  
        window.showLoader();  
      window.showAlert("Press Information is submittted","/Campaign");
    })
    .catch(e => {
      window.showLoader();  
      window.showAlert("OOps!!! Something went wrong","/Campaign_Press");
    });
  }
      });
  }else{
    window.showAlert("Please Login","/Login");
  }
