  validatePressInfo(){
  console.log("inside Press info validations")
      let input = this.state.input;
      let errors = {};
      let isValid = true;
   
      $("#PressinfoForm #prheader").each(function (index) {
        if (window.isEmpty($($("#PressinfoForm #prheader")[index]).val())) {
          isValid = false;
          errors["prheader"]= "Please enter your Header.";
        } 
        if (window.isEmpty($("#PressinfoForm").find("label[for=prbody]").closest("div").find("div").find("span[data-text=true]").text())) {
          isValid = false;
          errors["prbody"] = "Please enter body.";
        }

        if (window.isEmpty($($("#PressinfoForm #pruploadimage")[index]).val())) {
          isValid = false;
          errors["pruploadimage"] = "Please Upload a Image.";
        }
     
        if (window.isEmpty($($("#PressinfoForm #pruploadvideo")[index]).val())) {
          isValid = false;
          errors["pruploadvideo"] = "Please Upload a Video ";
        }
  
      });
      
     
       
      this.setState({
        errors: errors
      });
  
      return isValid;
  }
     
