function EmailCheck(email) {
  var mailformat = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,3}))$/;

  if ($(email).val().match(mailformat)) {
    return true;
  } else {
    if (!isEmpty($(email).val())) {
      /*changed*/
      showAlert("Invalid Email Id");
      $(email).val("");
      $(email).focus();
      return false;
    }
  }
}

function validURL(str) {
  var pattern = new RegExp('^(https?:\\/\\/)?' + // protocol
  '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|' + // domain name
  '((\\d{1,3}\\.){3}\\d{1,3}))' + // OR ip (v4) address
  '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*' + // port and path
  '(\\?[;&a-z\\d%_.~+=-]*)?' + // query string
  '(\\#[-a-z\\d_]*)?$', 'i'); // fragment locator

  var valueOfStr = $(str).val();

  if (pattern.test(valueOfStr)) {
    return true;
  } else {
    showAlert("Invalid URL Format");
    $(str).val("");
    $(str).focus();
    return false;
  }
}

function checkDateFormat(date) {
  var dateformat = /^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/;
  var txtVal = date.val();
  var day, month, year;
  var arr = [];

  if (txtVal.match(dateformat)) {
    if (txtVal.length < 10) {
      arr = txtVal.split('/');

      if (arr[0].length == 1) {
        day = arr[0].replace(arr[0], "0" + arr[0]);
      } else {
        day = arr[0];
      }

      if (arr[1].length == 1) {
        month = arr[1].replace(arr[1], "0" + arr[1]);
      } else {
        month = arr[1];
      }

      year = arr[2];
      date.val(day + "/" + month + "/" + year);
    }
  }

  if (!isEmpty(txtVal) && !isDate(txtVal)) {
    showAlert('Invalid Date');
    $(date).val("");
    $(date).focus();
  }
}

function isDate(txtDate) {
  var currVal = $(txtDate).val();
  if (currVal == '') return false;
  var rxDatePattern = /^(\d{1,2})(\/|-)(\d{1,2})(\/|-)(\d{4})$/;
  var dtArray = currVal.match(rxDatePattern);
  if (dtArray == null) return false;
  var dtDay = dtArray[1];
  var dtMonth = dtArray[3];
  var dtYear = dtArray[5];
  if (dtMonth < 1 || dtMonth > 12) return false;else if (dtDay < 1 || dtDay > 31) return false;else if ((dtMonth == 4 || dtMonth == 6 || dtMonth == 9 || dtMonth == 11) && dtDay == 31) return false;else if (dtMonth == 2) {
    var isleap = dtYear % 4 == 0 && (dtYear % 100 != 0 || dtYear % 400 == 0);
    if (dtDay > 29 || dtDay == 29 && !isleap) return false;
  }
  return true;
}

