      var blob = new Blob([data.PROFILE_PIC], { type: "images/*" });
      console.log(blob)
      formData.append("PROFILE_PIC",blob,data.PROFILE_NAME); 
      formData.append("STATUS",data.STATUS);
      formData.append("COMMENTS",data.COMMENTS);
      formData.append("DESCRIPTION",data.DESCRIPTION);
      formData.append("CREATED_USER",data.CREATED_USER);
      formData.append("CREATED_DATE",data.CREATED_DATE);
      formData.append("MODIFIED_USER",data.MODIFIED_USER);
      formData.append("MODIFIED_DATE",data.MODIFIED_DATE);
      // console.log(formData);
        return http.post(url, formData);//localhost:8000/api/mt_user(data)
      }

  upload(file, onUploadProgress) {
    let formData = new FormData();
    formData.append("file", file);
    return http.post("/upload", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
      onUploadProgress,
    });
  }


  getFiles() {
    return http.get("/files");
  }



}
